vec2 hats (float t){
	init;

    t = mod(t + 0., hbeat*0.5);

    sampleratereduction(t, 0.00005);
    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 1500.)*0.4;

    float amt = 0.2;
    float env = exp(-t*95.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((3000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    bitreduction(s, 0.4);
    s *= 0.5;
    ret;
}
vec2 hatsb (float t){
	init;

    t = mod(t + 0., hbeat/0.5);

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 5500.)*0.4;

    float amt = 1.;
    float env = exp(-t*2.4);
    float f =  sin(
        (2000. )*tt)*exp(-t*0.4)*0.215 
        +sin((9000. )*tt)*exp(-t*2.)*0.5 
        + ( 2250. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    float bitreduction = 0.75;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;

    s *= 0.7;
    ret;
}
void initKickPatt(float t){

    float r = beat/4.;

    float id = floor(t/r);


    for(int i = 0; i < 16; i++){

        vec4 r = r11(id + float(i));

    	pattK[i] = r.x < probK[i];

    }
}
vec2 mainSound( in int samp, float t )
{
    t += OFFS;
    //t += msr*4.;
    vec2 s= vec2(0);

    initKickPatt(t);

    float sc;

    vec2 k = kick(t,sc)*1.;
    vec2 sn = snare(t);

    vec2 ha = hats(t)*(pow(sc,4.4))*0.2;
    vec2 hb= hatsb(t)*(pow(sc,4.4))*0.1;
    vec2 bass = bass(t)*(pow(sc,1.4))*0.4;
    vec2 c = chord(t)*(pow(sc,1.5))*0.5;
    vec2 l = lead(t)*(pow(sc,0.9))*0.1;


    if(t < msr){
    	s += l;
        s += c;

    } else if (t < msr*2.){
    	s += l;
        s += hb;
        if(t > msr*1.75){
        	s += k;
        	s -= hb;
        	s -= ha;
        }

    } else {

        s += k;
        s += sn;
        s += ha;
        s += hb;
        s += bass;
        s += l;
        s += c;
    }

    s *= 0.4;

    s = mix(s,sign(s)*smoothstep(0.0,1.,abs(s)), (0.5 + sin(t)*0.)*1.);
    s = clamp(s,-1.,1.);

    //s *= smoothstep(0.,1.,t*0.5);
    return s;
}

