// Fork of "Voronoi edge tracking III" by michael0884. https://shadertoy.com/view/wdsczB
// 2020-03-28 00:37:58

float gauss(float x, float r)
{
    x/=r;
    return exp(-x*x);
}

ivec4 get(ivec2 p)
{
    return floatBitsToInt(texel(ch0, p));
}

edges gete(ivec2 p)
{
    edges e;
    ivec4 A = floatBitsToInt(texel(ch2, p));
    for(int i = 0; i < 4; i++)
    {
        e.ids[i] = A[i]%tot_n;
        e.ids[i+4] = A[i]/tot_n;
    }
    return e;
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

bool loadparticles(in edges e, inout vec2[8] xy)
{
    for(int i = 0; i < 8; i++)
    {
        if(e.ids[i] < 0) return false;
        xy[i] = getParticle(e.ids[i]).xy;
    }
    return true;
}

vec2 getCC(inout edges e, inout vec2[8] xy, int i) //get circumcenter
{
    int j = bitreverse3(i);
    ivec2 par = getParents(j);
    ivec2 id = ivec2((par.x + j)%8,(par.y + j)%8); 

    if(any(equal(ivec3(e.ids[j],e.ids[id.x],e.ids[id.y]),
                 ivec3(e.ids[id.x],e.ids[id.y],e.ids[j])))) return vec2(0.);

    //get the circumcenter for this branch of the binary tree
    vec2 cc = circumcenter(xy[j], xy[id.x], xy[id.y]);
 	return cc; 
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec4 load(ivec2 p)
{
    return texel(ch3, p);
}

vec3 getEdgeDistance(ivec2 p, vec2 pos)
{
    edges e = gete(p);
    vec2 xy[8];
    if(!loadparticles(e, xy)) return vec3(1e10);
    vec4 edge0 = vec4(getCC(e, xy, 2),getCC(e, xy, 3));
    vec4 side1 = vec4(getCC(e, xy, 4),getCC(e, xy, 5));
    vec4 side2 = vec4(getCC(e, xy, 6),getCC(e, xy, 7));
    float d = sdSegment(pos, edge0.xy, edge0.zw);
    vec2 data0 = load(ivec2(mix(edge0.xy, edge0.zw, 0.5))).zw;
    d = min(d, sdSegment(pos, edge0.xy, side1.xy));
    d = min(d, sdSegment(pos, edge0.xy, side2.xy));

    vec2 n = vec2(1.,-1)*(edge0.wz - edge0.yx);
    float tnormal = 0.5 - 0.5*clamp( dot(pos - edge0.xy, n) /
                           dot(n, n), -1., 1.); 

    vec2 data1 = 0.333*(load(ivec2(mix(edge0.xy, side1.xy, 0.5))).zw + 
        		 load(ivec2(mix(edge0.xy, side2.xy, 0.5))).zw +
        		 data0);

    d = min(d, sdSegment(pos, edge0.zw, side1.zw));
    d = min(d, sdSegment(pos, edge0.zw, side2.zw));
    vec2 data2 = 0.333*(load(ivec2(mix(edge0.zw, side1.zw, 0.5))).zw +
        		        load(ivec2(mix(edge0.zw, side2.zw, 0.5))).zw +
                        data0);

    float t = clamp( dot(pos - edge0.xy, edge0.zw - edge0.xy) /
                     dot(edge0.zw - edge0.xy, edge0.zw - edge0.xy), 0., 1.);
    vec2 data =  data1*clamp(1. - 2.*t, 0., 1.) +
                   data2*clamp(2.*t - 1., 0., 1.) + 
                   data0*clamp(1. - abs(2.*t - 1.), 0., 1.);
    return vec3(d, data);
}

#define scale 0.5

void mainImage( out vec4 fragColor, in vec2 pos )
{
    vec4 mouse = iMouse;
    if(mouse.z == 0.) mouse.xy = size*0.5 - size*scale*0.5;
    pos = pos*scale + mouse.xy;
    ivec2 p = ivec2(pos);

  	vec3 ce = getEdgeDistance(p, pos);
    float D = gauss(ce.x*ce.x*ce.x,0.2);
    fragColor =  D*(0.5 + sin(vec4(2,2,3,4)*ce.z));
}

