#define dt 0.5

#define loop(x) for(int i = min(0, iFrame); i < x; i++)


const int tot_n = N.x*N.y;

float hash11(float p)
{
    p = fract(p * 15.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p) - 0.5;
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

const int k = 1664525;  

ivec4 hash( ivec4 x )
{
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    return ivec4(x);
}

ivec2 i2xy(int id)
{
    return ivec2(id%N.x, id/N.x);
}

int xy2i(ivec2 p)
{
    return p.x + p.y*N.x;
}

ivec2 cross_distribution(int i)
{
    return (1<<2*(i/4)) * ivec2( ((i&2)/2)^1, (i&2)/2 ) * ( 2*(i%2) - 1 );
}

vec2 circumcenter(vec2 x, vec2 y, vec2 z)
{
    vec2 xy = y - x;
    vec2 xz = z - x;
    vec2 a = 0.5*(x+y);
    vec2 b = 0.5*(x+z);
    vec2 d = vec2(dot(xy,a), dot(xz,b));
    float det = determinant(mat2(xy, xz));
    return vec2(determinant(mat2(d.x, xy.y, d.y, xz.y)),determinant(mat2(xy.x, d.x, xz.x, d.y)))/det;
}

struct edges
{
    int ids[8];
};


//reverse the order of 3 bits
int bitreverse3(int i)
{
    return ((i&1)<<2) +
            (i&2) +
           ((i&4)>>2);    
}

ivec2 getParents(int i)
{
    if(i%2 == 0) return ivec2(-2,2);
    return ivec2(-1,1);
}

