float F(float d)
{
    return -1./(1.+0.05*d);
}

vec2 Fv(vec2 p0, vec2 p1)
{
    return normalize(p1-p0)*F(distance(p1,p0));
}

ivec4 get(ivec2 p)
{
    return floatBitsToInt(texel(ch0, p));
