void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);

    if(p.x > N.x || p.y > N.y) discard;

    pos = floor(pos);

    //this pixel value
    U = texel(ch1, pos);
    int id = xy2i(p);

    //neighbors
    ivec4 cp = get(ivec2(U.xy));
    vec4 p1 = getParticle(cp.y);
    vec4 p2 = getParticle(cp.z);
    vec4 p3 = getParticle(cp.w);

    vec2 F = Fv(U.xy, p1.xy) +
             Fv(U.xy, p2.xy) +
             Fv(U.xy, p3.xy);

    ivec4 mnb = get(ivec2(iMouse.xy));

    if(any(equal(ivec2(id),mnb.xy)) && iMouse.z>0.)
    {
        F += 0.0*(iMouse.xy - iMouse.zw); 
    }

    U.xy += F*dt;

    if(size.x - U.x < 2.) U.x = size.x - 2.;
    if(U.x < 2.) U.x = 2.;
    if(size.y - U.y < 2.) U.y = size.y - 2.;
    if(U.y < 2.) U.y = 2.;

    //this pixel value
    if(iFrame<1)
    {
        U.xy = size*hash22(pos);
        U.zw = 2.*(hash22(3.14159*pos) - 0.5);
    }

}

