//2nd order edge tracking
vec2 xy[8];     
edges E;
int n;

ivec4 GET(ivec2 p)
{
    return floatBitsToInt(texel(ch0, p));
}

edges get(ivec2 p)
{
    edges e;
    ivec4 A = floatBitsToInt(texel(ch2, p));
    for(int i = 0; i < 4; i++)
    {
        e.ids[i] = A[i]%tot_n;
        e.ids[i+4] = A[i]/tot_n;
    }
    return e;
}

vec4 save()
{
    return intBitsToFloat(ivec4(E.ids[0],E.ids[1],E.ids[2],E.ids[3]) + 
                          ivec4(E.ids[4],E.ids[5],E.ids[6],E.ids[7])*tot_n);
}


vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

bool isSame(ivec3 a, ivec3 b) //is the id triplet the same
{
    return any(equal(a.xxx,b)) && any(equal(a.yyy,b)) && any(equal(a.zzz,b));
}

bool isEdgeParticle(int id, vec2 pxy, ivec2 id2, vec4 nbxy)
{
    //edge point guess
    vec2 cc = circumcenter(nbxy.xy, nbxy.zw, pxy);
    //check the voronoi diagram at the guess
    if(cc.x > size.x-1. || cc.y > size.y-1. || cc.x < 0. || cc.y < 0.) return false;
    ivec4 t = GET(ivec2(cc));
    //the first 3 closest particles at the edge point pretty much always are the particles that produce the edge
    return isSame(t.xyz, ivec3(id2, id));
}



//edge store
bool store(int utemp)
{
	vec4 part = getParticle(utemp);
    for(int i = 0; i < 8; i++)
    {
        if(E.ids[i] == utemp) return false;
    }

    int j; int complete = 2; bool found = false;
	for(int i = min(2, iFrame+2); i < 8; i++)
    {
        j = bitreverse3(i);
        if(E.ids[j] >= 0) 
        {
            complete++;
            continue;
        }
        ivec2 par = getParents(j);
        ivec2 id = ivec2((par.x + j)%8,(par.y + j)%8); 
        ivec2 pid = ivec2(E.ids[id.x],E.ids[id.y]);
        vec4 pxy = vec4(xy[id.x],xy[id.y]);
        if(isEdgeParticle(utemp, part.xy, pid, pxy)) {
         	found = true;
            break;
        }
    }

    if(found)
   	for(int i = 0; i < 8; i++)
    {
        if(i==j)
        {
            E.ids[j] = utemp;
            xy[j] = part.xy;
        }
    }  

    return complete > 7;
}

bool storepos(ivec2 p)
{
    ivec4 nb = GET(p);
    loop(4)
    {
        if(store(nb[i])) return true;
    }
    return false;
}

bool storeE(in edges e)
{
    loop(8)
    {
    	if(store(e.ids[i])) return true;
    }
    return false;
}

bool storeedge(ivec2 p)
{
   edges e = get(p);
   return storeE(e);
}

void mainImage( out vec4 U, in vec2 fragCoord )
{
    vec2 pos = fragCoord;

    ivec2 p = ivec2(pos);

    for(int i = 0; i < 8; i++)
    {
        xy[i] = vec2(1e10);
        E.ids[i] = -1;
    }

    //set parents of the binary tree;
    ivec4 closest = GET(p);
    E.ids[0] = closest.x;
    E.ids[4] = closest.y;
    xy[0] = getParticle(closest.x).xy;
    xy[4] = getParticle(closest.y).xy;

    //jump flood sorting 

    //prev edge 
    edges e = get(p);

    bool complete = false;

    complete = complete||storepos(p);
    complete = complete||storeE(e); //resort this position

    loop(4)
    {
        if(complete) break;
        complete = complete||storeedge(p+cross_distribution(i));
        if(complete) break;
        complete = complete||store(hash(ivec4(p, iFrame, i)).x%tot_n); //random sort    
    }

    loop(8)
    {
        if(complete) break;
        complete = complete||storepos(p+cross_distribution(i));
    }

    if(!complete)
    {
        E = e;
    }

    U = save();
}

buffer_d_code:
//edge data tracker/sim - flows and stuff

vec4 data;
vec2 pos;

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}


bool loadparticles(in edges e, inout vec2[8] xy)
{
    for(int i = 0; i < 8; i++)
    {
        if(e.ids[i] < 0) return false;
        xy[i] = getParticle(e.ids[i]).xy;
    }
    return true;
}

vec2 getCC(inout edges e, inout vec2[8] xy, int i) //get circumcenter
{
    int j = bitreverse3(i);
    ivec2 par = getParents(j);
    ivec2 id = ivec2((par.x + j)%8,(par.y + j)%8); 

    if(any(equal(ivec3(e.ids[j],e.ids[id.x],e.ids[id.y]),
                 ivec3(e.ids[id.x],e.ids[id.y],e.ids[j])))) return vec2(0.);

    //get the circumcenter for this branch of the binary tree
    vec2 cc = circumcenter(xy[j], xy[id.x], xy[id.y]);
 	return cc; 
}

edges gete(ivec2 p)
{
    edges e;
    ivec4 A = floatBitsToInt(texel(ch2, p));
    for(int i = 0; i < 4; i++)
    {
        e.ids[i] = A[i]%tot_n;
        e.ids[i+4] = A[i]/tot_n;
    }
    return e;
}

vec4 load(ivec2 p)
{
    return texel(ch3, p);
}

vec2 getEdgeCenter(ivec2 p)
{
    edges e = gete(p);
    vec2 xy[8];
    if(!loadparticles(e, xy)) return vec2(1e10);
    return mix(getCC(e, xy, 2),getCC(e, xy, 3), 0.5);
}

bool isValid(inout vec2 cc)
{
    vec2 ec = getEdgeCenter(ivec2(cc));
    if(distance(ec, cc) > 1.) return false;
    cc = ec;
    return true;
}

void sort(vec2 ncc, vec2 d)
{
    if(isValid(ncc))
    {
        if(distance(ncc.xy, pos) < distance(data.xy, pos))
        {
            data.xy = ncc.xy;
            data.zw = d;
        }
    } 
}

void sortpos(ivec2 p)
{
    vec4 nb = load(p);
    sort(nb.xy, nb.zw);
}

void update(ivec2 p)
{    
    loop(4)
    {
        ivec4 rand = hash(ivec4(p, iFrame, i));
        ivec2 cp = ivec2(rand.x%int(size.x),rand.y%int(size.y));
        vec2 ec = getEdgeCenter(cp);
        sort(ec, vec2(10,0)*exp(-0.002*dot(ec - size*0.5,ec - size*0.5)));    
    }
}

void data_correction(ivec2 p)
{
    float lvl = 0.;
    vec4 nb0;
    loop(4) 
    {
        vec4 nb = load(p+cross_distribution(i));
        if(distance(data.xy, nb.xy) < 2. && //is the same EC
           distance(data.zw, nb.zw) > 1e-2) //data is different
        {
            nb0 = nb;
            lvl++;
        }
    }; 
    if(lvl > 2.) data=nb0; //if most neighbors are different
}

mat4x2 getEdgeData(ivec2 p)
{
    mat4x2 nec;
    edges e = gete(p);
    vec2 xy[8];
    if(!loadparticles(e, xy)) return mat4x2(0.);
    vec4 edge0 = vec4(getCC(e, xy, 2),getCC(e, xy, 3));
    vec4 side1 = vec4(getCC(e, xy, 4),getCC(e, xy, 5));
    vec4 side2 = vec4(getCC(e, xy, 6),getCC(e, xy, 7));

    nec[0] = mix(edge0.xy, side1.xy, 0.5);
    nec[1] = mix(edge0.zw, side1.zw, 0.5);
    nec[2] = mix(edge0.xy, side2.xy, 0.5);
    nec[3] = mix(edge0.zw, side2.zw, 0.5);

    mat4x2 ndata;

    for(int i = 0; i < 4; i++)
    {
        ndata[i] = load(ivec2(nec[i])).zw;
    }

    return ndata;
}


void mainImage( out vec4 fragColor, in vec2 coord )
{
    pos = coord;
    ivec2 p = ivec2(pos);

    //sort
    data = vec4(0.);
    sortpos(p);
    update(p);
    loop(8) sortpos(p+cross_distribution(i)); 
    data_correction(p);

    //simulation
    //get neighbors
    mat4x2 ndata = getEdgeData(ivec2(data.xy)); 

    //wave eq
    if(iFrame > 100)
    {
        float laplacian = -data.z;
        loop(4) laplacian += ndata[i].x*0.25;

        data.w = data.w*0.999 + 0.12*laplacian;
        data.z = data.z*0.99 + data.w*0.12;
    }

    fragColor = data;
}

