// BUF A,B (pingpong x2) x,y : particle pos / z : pressure
// BUF C,D (pingpong x2) rgb : ink
// the calc functions and simu params are in the Common tab

#define BUMP 1.0
#define COLOR_COEF 1.0
#define DIFF_COEF 0.4
#define SPEC_POWER 16.0
#define SPEC_COEF 0.4

void mainImage( out vec4 fragColor, in vec2 g )
{
    vec3 s = iResolution;

    vec2 uv = g/s.xy;
    vec2 uvc = (g-s.xy*0.5)/s.y;

    // normal
    float eps = 2.0/min(s.x, s.y);
	vec4 f = texture(iChannel0, uv); // ink
	vec4 fx = (texture(iChannel0, uv + vec2(eps,0))-f)/eps;
	vec4 fy = (texture(iChannel0, uv + vec2(0,eps))-f)/eps;
	vec3 nr = normalize( vec3(0., 0., 1) + vec3(fx.r, fy.r, 1.) * BUMP );           
	vec3 ng = normalize( vec3(0., 0., 1) + vec3(fx.g, fy.g, 1.) * BUMP );           
	vec3 nb = normalize( vec3(0., 0., 1) + vec3(fx.b, fy.b, 1.) * BUMP );           
	vec3 n = normalize(nr+ng+nb);

    float pr = texture(iChannel1, uv).z; // pressure waves

    // custom brdf, pressure is used for perturb normal in a cheap way for showing waves
	vec3 rd = normalize(vec3(uvc, -1.)); vec3 ld = vec3(0,0,1);
    float diff = max(dot(n + pr * 2., ld), 0.);  
	float spec = pow(max(dot(reflect(ld, n + pr * 0.15), rd), 0.),SPEC_POWER); 
	vec3 col = f.rgb * COLOR_COEF + diff * DIFF_COEF + spec * SPEC_COEF;

    fragColor = vec4(col, 1.0);
	fragColor = pow(fragColor, vec4(1.5));
	fragColor = clamp(fragColor, 0., 1.);
}

