#define emitForce 0.1
#define emitPosRadius 0.45
#define emitSize 0.01
#define countEmitters 4
#define visozity 1.0
#define emitAngleOffset 0.0
#define colorHash vec4(5,3,2,2)

// interesting result with :
// emitForce => 1.0
// countEmitters => 100
// emitAngleOffset => 1.5

vec4 CalcPositionPressure(sampler2D pre, vec2 g, vec3 s, int fr)
{
    vec4 res = vec4(0);

    vec2 uv = g/s.xy;
    vec2 uvc = (g-s.xy*0.5)/s.y;

    vec3 ts = 1./s;
    ts.z = 0.0;
    vec4 cur = texture(pre, uv);
	vec4 a = texture(pre, uv + ts.xz);
	vec4 b = texture(pre, uv + ts.zy);
	vec4 c = texture(pre, uv - ts.xz);
	vec4 d = texture(pre, uv - ts.zy);
	res = texture(pre, uv - cur.xy * ts.xy);
	vec2 gradPressure = vec2(a.z - c.z, b.z - d.z);
	float pres = (a.z + b.z + c.z + d.z) * 0.25f;
	float dive = 0.05 * (c.x - a.x + d.y - b.y);
	res.z = pres - dive; // pressure
	res.xy += gradPressure.xy * visozity; // particle pos

    float an = 0.0, st = 6.28318 / float(countEmitters);
    for (int i = 0; i < countEmitters;i++)
    {
        vec2 pos = vec2(cos(an),sin(an)) * emitPosRadius;
        if (length(uvc-pos) < emitSize)
		{
			res.xy = res.xy * (1.0f - emitForce) - 
                emitForce * vec2(cos(an + emitAngleOffset),sin(an + emitAngleOffset));
		}
        an += st;
    }

	if (uv.x <= ts.x || uv.y <= ts.y || uv.x >= 1.0f - ts.x || uv.y >= 1.0f - ts.y)
		res = vec4(0); //walls

    if (fr < 1)
		res = vec4(0);  // init

    return res;
}

vec4 CalcInk(sampler2D ink, sampler2D pre, vec2 g, vec3 s, int fr)
{
    vec4 res = vec4(0);

    vec2 uv = g/s.xy;
    vec2 uvc = (g-s.xy*0.5)/s.y;

    vec2 ts = 1./s.xy;
    vec4 cur = texture(pre, uv); // posX,posY,pre,notUsed
	res = texture(ink, uv - cur.xy * ts); // movement pos xy

    float an = 0.0, st = 6.28318 / float(countEmitters);
    for (int i = 0; i < countEmitters;i++)
    {
        vec2 dir = vec2(cos(an),sin(an));
        vec2 pos = dir * emitPosRadius;
        if (length(uvc-pos) < emitSize)
		{  
            res = sin((float(i) + colorHash.a) * colorHash) * 0.5 + 0.5;
        }
        an += st;
    }

	if (fr < 1) 
		res = vec4(0); // init

    return res;
}

