#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

struct Intersection { float distance; vec3 color; };

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

Intersection unionOp(Intersection a, Intersection b) {
    if (a.distance < b.distance) {
        return a;
    } else {
        return b;
    }
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

Intersection GetDist(vec3 p) {
	vec4 s = vec4(-1, 1, 0, 1);

    Intersection sphereDist = Intersection(length(p-s.xyz)-s.w, vec3(0.8, 0.4, 0.1));
    vec3 lightBulbPos = p-vec3(0, 5.5, -2);
    lightBulbPos.xz -= vec2(sin(iTime), cos(iTime))*3.;
    Intersection lightBulbDist = Intersection(length(lightBulbPos)-0.4,vec3(0.8, 0.7, 0.4));
    Intersection planeDist = Intersection(p.y, vec3(0.1, 0.2, 0.9));
    Intersection boxDist = Intersection(sdBox(p - vec3(1, 1, 0), vec3(.5, .5, .5)), vec3(0.1, 0.6, 0.2));

    Intersection d = Intersection(1e10, vec3(0));
    d = unionOp(d, planeDist);
    d = unionOp(d, sphereDist);
    d = unionOp(d, lightBulbDist);
    d = unionOp(d, boxDist);
    return d;
}


Intersection RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    Intersection intersection;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        intersection = GetDist(p);
        float dS = intersection.distance;
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return Intersection(dO, intersection.color);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p).distance;
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).distance,
        GetDist(p-e.yxy).distance,
        GetDist(p-e.yyx).distance);

    return normalize(n);
}

Intersection GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, -2);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*3.;
    vec3 lightRay = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    // surfaces perpendicular to the light are more illuminated
    float diffuse = clamp(dot(n, lightRay), 0., 1.);

    // if there's some object between our point and the light, reduce the diffuse light
    Intersection i = RayMarch(p+n*SURF_DIST*2., lightRay);
    if(i.distance < length(lightPos-p)) {
        diffuse *= .1;
    }

    return Intersection(diffuse, i.color);
}


