vec2 getCC(inout edges e, inout vec2[8] xy, int i) //get circumcenter
{
    int j = bitreverse3(i);
    ivec2 par = getParents(j);
    ivec2 id = ivec2((par.x + j)%8,(par.y + j)%8); 
    //get the circumcenter for this branch of the binary tree
    if(any(equal(ivec3(e.ids[j],e.ids[id.x],e.ids[id.y]),
                 ivec3(e.ids[id.x],e.ids[id.y],e.ids[j])))) return vec2(0.);
    vec2 cc = circumcenter(xy[j], xy[id.x], xy[id.y]);
