//reverse the order of 3 bits
int bitreverse3(int i)
{
    return ((i&1)<<2) +
            (i&2) +
           ((i&4)>>2);    
}

ivec2 getParents(int i)
{
    if(i%2 == 0) return ivec2(-2,2);
    return ivec2(-1,1);
}

