vec2 chord(float t){
	init;


    sampleratereduction(t, 0.00001);
    float r = bar;
    t = mod(t,r*4.);
    int id = int(floor(t/r));

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 4500.)*0.45;

    nn *= 0.1*sin(t);
    float m = 1.;
    float width = 10.;
    s += sin( note(chords[id].x, 3.) *tt + nn.x);
    s += sin( note(chords[id].y, 3.) *tt + nn.x);
    for(float i = 0.; i < 4.; i++){
        float a = (1. + i/4.);
    	//s += sin( note(chords[id].x, 3.) *tt + nn.x)*m;
    	s.x += sin( note(chords[id].x, 4.) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].y, 4.) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].z, 4.) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].w, 4.) *tt*a + nn.x)*m;
    	s.y += sin( note(chords[id].x, 4.) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].y, 4.) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].z, 4.) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].w, 4.) *tt*a + nn.y + width)*m;
    	m *= 0.5;
    }

    //bitreduction(s, 0.04);

    s *= 0.03;
    s = sin(s*2.);
    //s /= 2.;
    //s = sin(s);
	ret;
}


vec2 hats (float t){
	init;

    t = mod(t + 0., hbeat*0.5);

    sampleratereduction(t, 0.00005);
    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 1500.)*0.4;

    float amt = 0.2;
    float env = exp(-t*15.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((9000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    bitreduction(s, 0.8);
    s *= 0.5;
    ret;
}
vec2 hatsb (float t){
	init;

    t = mod(t + 0., hbeat/0.5);

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 5500.)*0.4;

    float amt = 1.;
    float env = exp(-t*10.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((9000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    float bitreduction = 0.35;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;

    s *= 0.7;
    ret;
}
void initKickPatt(float t){

    float r = beat/4.;

    float id = floor(t/r);


    for(int i = 0; i < 16; i++){

        vec4 r = r11(id + float(i));

    	pattK[i] = r.x < probK[i];

    }
}

vec2 mainSound( in int samp, float t )
{
    //t += msr*4.;
    vec2 s= vec2(0);

    initKickPatt(t);

    float sc;

    vec2 k = kick(t,sc)*1.;
    vec2 sn = snare(t);

    vec2 ha = hats(t)*(pow(sc,4.4))*0.2;
    vec2 hb= hatsb(t)*(pow(sc,4.4))*0.1;
    vec2 bass = bass(t)*(pow(sc,1.4))*0.4;
    vec2 c = chord(t)*(pow(sc,2.8))*0.7;
    vec2 l = lead(t)*(pow(sc,1.))*0.25;


    if(t < msr){
        t = mod(t,msr);
    	s += c;

        if(t > bar){
    		s += bass;	
        }
        if(t > bar + beat){
        	s += l;
        }
        if(t > beat*14.){
    		s += k;
        }
    } else if (t < msr*2.){
    	s += k;
    	s += sn;
        s += ha;
        s += c;
        s += bass;	
        //s += l;
    } else if (t < msr*4.){
        t = mod(t,msr);
    	s += k;
    	s += sn;
        s += ha;
        s += hb;
        s += c;
        s += bass;	
        if(t > msr*3.5){
        	s -= c;
        	s += l;
        }
    } else {
        t -= msr*4.;
    	s += k;
    	s += sn;
        s += ha;
        s += hb;
        s += c;
        s += bass;	
        //s -= c;
        s += l;
    	bitreduction(s, (0.01 + smoothstep(0.,1.,t*0.01)));
    }

    s *= 0.1;

    s = mix(s,sign(s)*smoothstep(0.1,1.,abs(s)), (0.3 + sin(t)*0.2)*0.);
    s = clamp(s,-1.,1.);

    //s *= smoothstep(0.,1.,t*0.5);
    return s;
}

