#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float getDist(vec3 p){
	vec4 s = vec4(0., 1., 6., 1.);
    float sphereDist = length(p-s.xyz)-s.w;
    float planeDist = p.y;
    float d = min(sphereDist, planeDist);

    return d;
}

float rayMarch(vec3 ro, vec3 rd){
	float dO = 0.;

  	for(int i = 0; i < MAX_STEPS; i++){    	
   		vec3 p = ro + rd*dO;
        float ds = getDist(p);
        dO += ds;
        if(ds < SURF_DIST || dO > MAX_DIST) break;
    }

    return dO;
}
vec3 getNormal(vec3 p){
	vec2 e = vec2(.01, .0);
    float d = getDist(p);
    vec3 n = d - vec3(
        getDist(p - e.xyy),
        getDist(p - e.yxy),
        getDist(p - e.yyx));
    return normalize(n);
}

float getLight(vec3 p){
 	vec3 lightPos = vec3(0., 5., 6.);
    lightPos.xz += vec2(cos(iTime), sin(iTime))*2.;
    vec3 l = normalize(lightPos - p);
    vec3 n = getNormal(p);
    float diff = clamp(dot(l, n), 0., 1.);

    float d = rayMarch(p+n*SURF_DIST*2., l);
    if(d < length(p-lightPos)) diff*= .1;
    return diff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro =  vec3(0., 1., 0.);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));

    float d = rayMarch(ro, rd);
 	vec3 p = ro + rd*d;

    col = vec3(getLight(p));


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(54312.1031, -9137.1030, 6148.0973));
    p3 += dot(p3, p3.yzx+33.33);
    return -1.0 + 2.0 * fract((p3.xx+p3.yz)*p3.zy);
}

vec2 map(vec2 uv)
{
    float c = 0.;
    float t = 0.2 * iTime + 100.0;

    float md = 100.;

    for (float id = 0.; id < 50.; ++id)
    {        
        vec2 h = hash22(vec2(id));
        vec2 p = (0.5 * sin(h * t) + 0.25 * sin(2. * h * t)) / 0.75;

        float d = length(p - uv);

        float c1 = 1. - smoothstep(0.0, 0.075, d);

        c += c1;

        md = min(d, md);        
    }

    return vec2(c, md);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.xy;

    vec2 m = map(uv);

    float eps = 0.001;
    vec3 norm = normalize(vec3(map(uv + vec2(eps, 0.)).y - m.y,
                	map(uv + vec2(0., eps)).y - m.y,
                    -eps));

    vec3 dift = vec3(0.3176, 0.4765, 0.506) * max(0., dot(vec3(sin(iTime), 0.9, 0.1), norm));
    vec3 difb = vec3(0.988, 0.839, 0.545) * max(0., dot(vec3(cos(iTime), -0.6, 0.1), norm));

    vec3 diff = (0.5 * m.y) * (dift + difb);

    fragColor = vec4(diff + 0.2 * m.x + vec3(m.y), 1.0);
}

// "Linked Rings 2" by dr2 - 2020
