#define hr vec2(1.,sqrt(3.))

vec4 hexagrid(vec2 uv,float detail){
    uv*=detail;
    vec2 ga = mod(uv,hr)-hr*.5;
    vec2 gb = mod(uv-hr*0.5,hr) - hr*.5;
    vec2 guv = (dot(ga,ga) < dot(gb,gb)) ? ga : gb;
    vec2 gid = uv - guv;
    return vec4(guv,gid);
}
