    float f = smoothstep(0.,1.,fbm(j*80.).x);

    f = pow(f, 6.)*2.4;
    if(col.x < 0.4)
        col += f*6.5;


    col -= f;



    //col += fbm(p).x*10.;
    //col.r += sin(id)*3.5;
	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    float aa = 5.;

    for(float i =0.; i < aa*aa   ; i++){
    	col += get(fragCoord + vec2(mod(i,aa),floor(i/aa))/aa);
    }
    col /= aa*aa;


    col = max(col, 0.);
	col = pow(col, vec3(0.4545));

    col = 1. - col;

    fragColor = vec4(col,1.0);
}

