vec2 mainSound( in int samp, float t )
{
    vec2 s= vec2(0);


    float sc;
    s += kick(t,sc)*1.;
    s += snare(t);
    s += hats(t)*(pow(sc,4.4))*0.2;
    s += hatsb(t)*(pow(sc,4.4))*0.2;
    s += chord(t)*(pow(sc,1.8))*0.9;
    s += lead(t)*(pow(sc,1.))*0.25;

    if(t > beat*16. &&  (  t < beat*(16. + 8.) || t > beat*(16. + 12.) )  )
    	s += bass(t)*(pow(sc,1.))*0.75;
