    s += sin( note(n, 1.) *tt 
             +sin(note(n, 2.) *tt + 
             	+sin(note(n, 4.) *tt)*exp(-t*1.)*0.1 

                 )*exp(-t*9.)*1. 
             +sin(note(n, 1.) *tt + 
             	+sin(note(n, 2.) *tt)*exp(-t*1.)*0.1 

                 )*exp(-t*9.)*1.5
            )*env;


    s *= 1.;


    //s *= float(pattL[int(id.y)]);

    //s += sin( f )*env;
    //s += amt*sin( f + nn*0.5 )*env;
    //s -= amt*sin( f )*env;

    s *= 0.7;
