}



vec2 chord(float t){
	init;


    sampleratereduction(t, 0.0001);
    float r = bar;
    t = mod(t,r*4.);
    int id = int(floor(t/r));

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 4500.)*0.45;

    nn *= 0.1*sin(t);
    float m = 1.;
    float width = 2.;
    for(float i = 0.; i < 9.; i++){
        float a = (1. + i/3.);
    	s.x += sin( note(chords[id].x, 4.) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].y, 4.) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].z, 4.) *tt*a + nn.x)*m;
    	s.x += sin( note(chords[id].w, 4.) *tt*a + nn.x)*m;
    	s.y += sin( note(chords[id].x, 4.) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].y, 4.) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].z, 4.) *tt*a + nn.y + width)*m;
    	s.y += sin( note(chords[id].w, 4.) *tt*a + nn.y + width)*m;
    	m *= 0.5;
    }

    bitreduction(s, 0.001);

    s *= 0.04;
    //s /= 2.;
    //s = sin(s);
	ret;
}


vec2 hats (float t){
	init;

    t = mod(t + 0., hbeat*0.5);

    sampleratereduction(t, 0.00005);
    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 1500.)*0.4;

    float amt = 0.7;
    float env = exp(-t*15.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((9000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    bitreduction(s, 0.5);
    s *= 0.7;
    ret;
}
vec2 hatsb (float t){
	init;

    t = mod(t + 0., hbeat/0.5);

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 5500.)*0.4;

    float amt = 1.;
    float env = exp(-t*5.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((9000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    float bitreduction = 0.35;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;

    s *= 0.7;
    ret;
}


vec2 mainSound( in int samp, float t )
{
    vec2 s= vec2(0);


    float sc;
    s += kick(t,sc)*1.;
    s += snare(t);
    s += hats(t)*(pow(sc,4.4))*0.1;
    s += chord(t)*(pow(sc,1.8))*0.45;
    s += lead(t)*(pow(sc,1.))*0.25;
    s += bass(t)*(pow(sc,1.))*0.5;
    //s += lead(t)*0.4;
    s *= 0.7;

    s = mix(s,sign(s)*smoothstep(0.1,1.,abs(s)), 0.2 + sin(t)*0.);
    s = clamp(s,-1.,1.);

    //s *= smoothstep(0.,1.,t*0.5);
    return s;
}

