#define pmod(p,x) mod(p,x) - 0.5*x
#define pal(a,b,c,d,e) ((a) + (b)*sin((c)*(d) + (e)))
#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))


vec4 valueNoise(vec2 t, float w){
    vec2 fr = fract(t);
	return 
        mix(
            mix( 
                texture(iChannel0,vec2(floor(t.x), floor(t.y))/256.),
                texture(iChannel0,vec2(floor(t.x), floor(t.y) + 1.)/256.),
            	smoothstep(0.,1.,fr.y)
            ),
            mix( 
                texture(iChannel0,vec2(floor(t.x) + 1.,floor(t.y))/256.),
                texture(iChannel0,vec2(floor(t.x) + 1.,floor(t.y) + 1.)/256.),
            	smoothstep(0.,1.,fr.y)
            ),
            smoothstep(0.,1.,pow(fr.x, w)));
}
vec4 fbm(vec2 uv){
	vec4 n = vec4(0);


    n += valueNoise(uv*500.,0.1);
    //n += valueNoise(uv*1000., 0.1)*0.5;
    n += valueNoise(uv*1700.,0.1)*0.5;

    n -= valueNoise(uv*10.,1.)*1.;
    n -= valueNoise(uv*20.,0.5)*0.5;


    n = max(n, 0.);
    return n;
}


vec3 get(vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
	vec2 uvn = uv;
    uv *= 1. - dot(uv,uv)*0.3;

    uv *= 2. + valueNoise(vec2(iTime), 0.2).x*1.5;

    vec3 col = vec3(0);



    for(int i = 0; i<3; i++){
        if(uv.x < uv.y){uv.xy = uv.yx;}
        uv *= rot(0.5);
        uv.x -= 0.9;

    }

    float m = 1.;
    for(float i = 0.; i< 1.; i++){
        //uv = uv.yx;
    	float dpp = dot(uv,uv);
        dpp = clamp(dpp, 0.1,1.);

        vec2 z1 = vec2(0.4);
        vec2 z2 = vec2(-0.1);
		z1 = uv - z1; uv -= z2;        
    	uv = mat2(z1, z1.y, -z1.x)*(uv*1. + 1.*vec2(-0.1, 0.7))/ dpp; 

        uv -= vec2(0.1,0.);
    }

    uv = sin(uv*3.14*(0.6));

    float dC = log(length(uv) + 90.)+ iTime*0.001;


    float modD = 0.00099;
    float id = floor(dC/modD);
    dC = pmod(dC, modD);


    vec3 c = pal(0.5,0.5,vec3(0.1,0.6,0.9), 0.6 + sin(id*0.4)*0.4, id*0.9 + iTime*0.5);

    c = max(c, 0.);
    c = smoothstep(0.,1.,c);
    col += c;



    //col -= fbm(uv).x*0.001;
    //col += fbm(uv + 4.).x*0.001;


    col = pow(col, vec3(1. + dot(uvn,uvn)));

    col *= 1.  - dot(uvn,uvn);

    //col += smoothstep(0.01);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    // float(min(iFrame,0)) hack for faster compilation (stops loop unrolling)
    for(float i =0.; i < 9. + float(min(iFrame,0)); i++){
    	col += get(fragCoord + vec2(mod(i,3.),floor(i/3.))*0.25);
    }
    col /= 9.;

	col = pow(col, vec3(0.4545));

    fragColor = vec4(col,1.0);
}

