#define txFnt iChannel0

float PrTorusDf (vec3 p, float ri, float rc);
vec3 HsvToRgb (vec3 c);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
void DTimeSet (vec4 d);
int GetTxChar (int _nc);
float Fbm2 (vec2 p);

vec3 ltDir, vnCylIn;
vec2 fntSize, qnFnt, qnTxt;
float dstFar, tCur, rngBlk, bCylRad, bCylHt, dCylIn, dCylOut;
int idTxt;
const float pi = 3.14159;

void CylHit (vec3 ro, vec3 rd)
{
  vec3 s;
  float a, ai, b, w, ws, srdy;
  dCylIn = dstFar;
  dCylOut = dstFar;
  vnCylIn = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    ws = sqrt (w);
    srdy = sign (rd.y);
    if (a > 0.) {
      ai =  1. / a;
      dCylIn = (- b - ws) * ai;
      dCylOut = (- b + ws) * ai;
    }
    if (a > 0.) s = ro + dCylIn * rd;
    else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vnCylIn.xz = s.xz / bCylRad;
    else if (srdy * ro.y < - bCylHt) {
      dCylIn = - (srdy * ro.y + bCylHt) / abs (rd.y);
      if (length (ro.xz + dCylIn * rd.xz) < bCylRad) vnCylIn.y = - srdy;
      else dCylIn = dstFar;
    } else dCylIn = dstFar;
    if (dCylIn < dstFar) {
      if (a > 0.) s = ro + dCylOut * rd;
      else s.y = bCylHt;
      if (abs (s.y) > bCylHt && srdy * ro.y < bCylHt)
         dCylOut = (- srdy * ro.y + bCylHt) / abs (rd.y);
    }
  }
}

float FontTexDf (vec2 p)
{
  vec3 tx;
  ivec2 ip;
  float d;
  int ic;
  ic = 0;
  ip = ivec2 (floor (p));
  if (ip.x == 0 && ip.y == 0) ic = GetTxChar (idTxt);
  if (ic != 0) {
    tx = texture (txFnt, mod ((vec2 (mod (float (ic), 16.),
       15. - floor (float (ic) / 16.)) + fract (p)) * (1. / 16.), 1.)).gba - 0.5;
    qnFnt = vec2 (tx.r, - tx.g);
    d = tx.b + 1. / 256.;
  } else d = 1.;
  return d;
}

vec3 RdNZero (vec3 rd)
{
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  return rd;
}

float ObjRayT (vec3 ro, vec3 rd)
{
  vec3 p, rdi;
  vec2 srd, h;
  float dHit, dLim, d;
  RdNZero (rd);
  srd = - sign (rd.xy);
  rdi = 1. / abs (rd.xyz);
  dHit = 0.;
  dLim = rngBlk;
  ro.xy /= fntSize.x;
  rd.xy /= fntSize.x;
  ro.xy += 0.5;
  for (int j = 0; j < 150; j ++) {
    p = ro + dHit * rd;
    h = rdi.xy * fract (srd * p.xy);
    d = max (min (fntSize.x, 1.) * FontTexDf (p.xy), abs (p.z) - 0.5 * fntSize.y);
    dHit += min (d, 0.01 + fntSize.x * min (h.x, h.y));
    if (d < 0.0001 || dHit > dLim) break;
  }
  if (d >= 0.0001) dHit = dstFar;
  return dHit;
}

float BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  RdNZero (rd);
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  dMin = dstFar;
  if (df > 0. && dn < df) {
    dMin = dn;
    rngBlk = df - dn;
  }
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float cRad;
  cRad = 0.04;
  q = p;
  q.y = abs (q.y) - bCylHt + cRad;
  return PrTorusDf (q.xzy, cRad, bCylRad - cRad);
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 10. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 BgCol (vec3 rd)
{
  return StarPat (rd, 12.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, qtTxt, tro, trd, trdd, q, qt, ds;
  float dstTxt, dstBlk, dstObj, db, d, ang, angTxt, angRot, aTw, rad;
  int idT;
  fntSize = vec2 (1.7, 0.6);
  dstTxt = dstFar;
  tro = ro;
  trd = rd;
  rad = 6.;
  bCylRad = rad + 0.4 * fntSize.x;
  bCylHt = 1.4 * fntSize.y;
  dstObj = ObjRay (ro, rd);
  CylHit (ro, rd);
  if (dCylIn < dstFar) {
    angRot = 0.05 * pi * tCur;
    aTw = angRot;
    tro.xz = Rot2D (tro.xz, angRot);
    trd.xz = Rot2D (trd.xz, angRot);
    for (float k = 0.; k < 42.; k ++) {
      idTxt = int (k);
      ang = 2. * pi * (1. - k / 42.);
      qt = - rad * vec3 (sin (ang + vec2 (0., 0.5 * pi)), 0.).xzy;
      q = tro - qt;
      q.xz = Rot2D (q.xz, ang);
      q.yz = Rot2D (q.yz, ang + aTw);
      trdd = trd;
      trdd.xz = Rot2D (trdd.xz, ang);
      trdd.yz = Rot2D (trdd.yz, ang + aTw);
      db = BlkHit (q, trdd, fntSize.xxy * vec3 (0.5, 0.55, 0.55));
      if (db < dstFar) {
        d = db + ObjRayT (q + db * trdd, trdd);
        if (d < dstTxt) {
          dstTxt = d;
          qtTxt = qt;
          angTxt = ang;
          qnTxt = qnFnt;
          idT = idTxt;
        }
      }
    }
  }
  if (min (dstTxt, dstObj) < dstFar) {
    if (dstTxt < dstObj) {
      tro += dstTxt * trd;
      ds = tro - qtTxt;
      ds.xz = Rot2D (ds.xz, angTxt);
      ds.yz = Rot2D (ds.yz, angTxt + aTw);
      col = HsvToRgb (vec3 (mod (0.05 * tCur + ((idT > 25) ? 0.5 : 0.), 1.), 1., 1.));
      if (abs (ds.z) < 0.49 * fntSize.y) {
        vn = normalize (vec3 (qnTxt, 0.00001));
        col = mix (col, vec3 (1.), 0.7);
      } else {
        vn = vec3 (0., 0., sign (ds.z));
      }
      vn.yz = Rot2D (vn.yz, - angTxt - aTw);
      vn.xz = Rot2D (vn.xz, - angRot - angTxt);
      col = col * (0.2 + 0.8 * max (dot (ltDir, vn), 0.)) +
         0.5 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
      col = mix (col, BgCol (reflect (rd, vn)), 0.7 - 0.5 * abs (dot (rd, vn)));
    } else if (dstObj < dstFar) {
      vn = ObjNf (ro + dstObj * rd);
      col = vec3 (0.5, 0.5, 0.6) * (0.2 + 0.8 * max (dot (ltDir, vn), 0.)) +
         0.5 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    }
  } else col = BgCol (rd);
  if (dCylIn < dstFar) {
    col = mix (col,  vec3 (1.) * (0.2 + 0.8 * max (dot (ltDir, vnCylIn), 0.) +
       0.5 * pow (max (dot (normalize (ltDir - rd), vnCylIn), 0.), 32.)), 0.2);
    col = mix (col, BgCol (reflect (rd, vnCylIn)), 0.8 * pow (1. - abs (dot (rd, vnCylIn)), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd;
  vec2 canvas, uv;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.06 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  vuMat = StdVuMat (el, az);
  rd = vuMat * normalize (vec3 (uv, 3.));
  ro = vuMat * vec3 (0., -0.3, -15.);
  ltDir = vuMat * normalize (vec3 (0.3, 1., -1.));
  dstFar = 100.;
  DTimeSet (dateCur);
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

