int DWk (ivec3 ymd) // "Zeller's congruence"
{
  ivec2 cy;
  int s;
  ymd.y += 1;
  if (ymd.y <= 2) {
    ymd.x -= 1;
    ymd.y += 12;
  }
  cy.x = ymd.x / 100;
  cy.y = ymd.x - 100 * cy.x;
  s = ymd.z + 13 * (ymd.y + 1) / 5 + cy.y + cy.y / 4 + cy.x / 4 + 5 * cy.x;
  s -= 7 * (s / 7) - 6;
  s -= 7 * (s / 7);
  return s;
}

int DElaps (ivec3 ymd) // Days from 1970-01-01 (http://howardhinnant.github.io/date_algorithms.html)
{
  int era, yoe, doy, doe;
  if (ymd.y <= 2) -- ymd.x;
  era = (ymd.x >= 0 ? ymd.x : ymd.x - 399) / 400;
  yoe = (ymd.x - era * 400);
  doy = (153 * (ymd.y + ((ymd.y > 2) ? -3 : 9)) + 2) / 5 + ymd.z - 1;  // [0-365]
  doe = yoe * 365 + yoe / 4 - yoe / 100 + doy;  // [0-146096]
  return era * 146097 - 719468 + doe;
}

