const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

////////////////////////////////////////////////////////////////////////////////
//
// Sierpinski Gasket on a sphere, with some randomization.
//
// <mouse>: rotation
// <up>/<down>: move in/out
// <page up>/<page down>: color change probability
// <left>/<right>: triangle division depth
// c: color
// g: display gasket only
// r: autorotation
//
////////////////////////////////////////////////////////////////////////////////

const float AA = 2.0;
bool dorotate = true;
bool gasketonly = false;
bool docolor = true;
int depth = 10; // Levels of triangle
float pcolorchange = 0.25; // Probability of color change at a given level

const float PI = 3.14159265;

void assert(bool b);
vec3 hsv2rgb(vec3 c);
