#define size iResolution.xy
#define pixel(a, p) texture(a, p/vec2(textureSize(a,0)))
#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2
#define ch3 iChannel3
#define PI 3.14159265

#define N ivec2(256,128)
#define dt 0.25


const int tot_n = N.x*N.y;

float hash11(float p)
{
    p = fract(p * 15.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p) - 0.5;
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

const int k = 1664525;  

ivec4 hash( ivec4 x )
{
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    return ivec4(x);
}

ivec2 i2xy(int id)
{
    return ivec2(id%N.x, id/N.x);
}

int xy2i(ivec2 p)
{
    return p.x + p.y*N.x;
}

ivec2 cross_distribution(int i)
{
    return (1<<(i/4)) * ivec2( ((i&2)/2)^1, (i&2)/2 ) * ( 2*(i%2) - 1 );
}

buffer_a_code:
//particle buffer

ivec4 get(int id)
{
    return floatBitsToInt(texel(ch0, i2xy(id)));
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float F(float d)
{
    return 1./(0.1*d*d+1.) - 1.*exp(-1.*d);
}

vec2 Fv(vec2 p0, int pid)
{
    if(pid < 0) return vec2(0); 
   	vec2 p1 = getParticle(pid).xy;
    return normalize(p1-p0)*F(distance(p1,p0));
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);
        int id = xy2i(p);

        //this pixel value
        if(iFrame<10)
        {
            U.xy = size*hash22(pos);
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
      		return;
        }

        //neighbors
   		ivec4 cp = get(id);

        vec2 F = Fv(U.xy, cp.x) +
            	 Fv(U.xy, cp.y) +
            	 Fv(U.xy, cp.z) +
                 Fv(U.xy, cp.w) + 
            	 vec2(0,-0.003);

        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F += 2.*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        U.zw = U.zw*0.999 + F*dt;
        U.xy += U.zw*dt;

        //border conditions
        if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);


    }
    else discard;
}

buffer_b_code:
//sorting closest 4 particles in axis directions that make a bounding box
//only in particle space, texture buffer not needed

ivec4 get(int id)
{
    return floatBitsToInt(texel(ch0, i2xy(id)));
}

vec4 save(ivec4 v)
{
    return intBitsToFloat(v);
}

ivec4 u; //ids
vec4 d; //distances
vec2 pos; //this particle position
int tid;

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

//insertion sort
void sort(int utemp)
{
    if(utemp == tid) return;

   	vec4 part = getParticle(utemp);
    vec2 dx = part.xy - pos;
    float dtemp = length(dx);
    //sorting
    if(dx.x > abs(dx.y))
    {
        if(d.x > dtemp) 
        {
            d.x = dtemp;
        	u.x = utemp;
        }
    }
    else if(dx.x < -abs(dx.y))
    {
        if(d.y > dtemp) 
        {
            d.y = dtemp;
        	u.y = utemp;
        }
    }
    else if(dx.y > abs(dx.x))
    {
        if(d.z > dtemp) 
        {
            d.z = dtemp;
        	u.z = utemp;
        }
    }
    else if(d.w > dtemp) 
    {
        d.w = dtemp;
        u.w = utemp;
    }
}

void sortneighbor(int id)
{
    ivec4 nb = get(id);
    for(int j = 0; j < 4; j++)
    {
        sort(nb[j]);
    }
}

void mainImage( out vec4 U, in vec2 fragCoord )
{  
    ivec2 p = ivec2(fragCoord);
    if(p.x > N.x || p.y > N.y) discard;

    int id = xy2i(p);

    u = ivec4(-1); d = vec4(1e10); 

    tid = id;
    pos = getParticle(id).xy;

    for(int i = 0; i < 8; i++)
    {
        sort(hash(ivec4(p, iFrame, i)).x%tot_n); //random sort    
    }

    sortneighbor(id); 
    ivec4 nb = get(id);
    for(int i = 0; i < 4; i++)
    {
        sortneighbor(nb[i]); 
    }



    U = save(u);
}

buffer_c_code:
//4th order voronoi particle tracking 

ivec4 get(ivec2 p)
{
    return floatBitsToInt(texel(ch0, p));
}

vec4 save(ivec4 v)
{
    return intBitsToFloat(v);
}

ivec4 u; //ids
vec4 d; //distances
vec2 pos; //pixel position

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

//insertion sort
void sort(int utemp)
{
   	float dtemp = particleDistance(utemp, pos);
    //sorting
    if(d.x > dtemp)
    {
        d = vec4(dtemp, d.xyz);
        u = ivec4(utemp, u.xyz);
    }
    else if(d.y > dtemp && dtemp > d.x)
    {
        d.yzw = vec3(dtemp, d.yz);
        u.yzw = ivec3(utemp, u.yz);
    }
    else if(d.z > dtemp && dtemp > d.y)
    {
        d.zw = vec2(dtemp, d.z);
        u.zw = ivec2(utemp, u.z);
    }
    else if(d.w > dtemp && dtemp > d.z)
    {
        d.w = dtemp;
        u.w = utemp;
    }
}

void sortpos(ivec2 p)
{
    ivec4 nb = get(p);
    for(int j = 0; j < 4; j++)
    {
        sort(nb[j]);
    }
}

void mainImage( out vec4 U, in vec2 fragCoord )
{
    pos = fragCoord;
    ivec2 p = ivec2(pos);

    u = ivec4(-1); d = vec4(1e10); 

    //jump flood sorting 
    sortpos(p); //resort this position
    for(int i = 0; i < 8; i++)
    {
        sortpos(p+cross_distribution(i)); 
    }

    for(int i = 0; i < 4; i++)
    {
        sort(hash(ivec4(p, iFrame, i)).x%tot_n); //random sort    
    }

    U = save(u);
}

