// radiual blur in this buffer

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
	vec2 uvn = (fragCoord - 0.5*iResolution.xy)/iResolution.xy;


    // Radial blur
    float steps = 20.;
    float scale = 0.00 + pow(length(uv - 0.5),1.8)*0.06;
    //float chromAb = smoothstep(0.,1.,pow(length(uv - 0.5), 0.3))*1.1;
    float chromAb = pow(length(uv - 0.5),1.)*3.3;
    vec2 offs = vec2(0);
    vec4 radial = vec4(0);
    for(float i = 0.; i < steps; i++){
        scale *= 0.97;
        vec2 target = uv + offs;
        offs -= normalize(uvn)*scale/steps;
    	radial.r += texture(iChannel0, target + chromAb*1./iResolution.xy).x;
    	radial.g += texture(iChannel0, target).y;
    	radial.b += texture(iChannel0, target - chromAb*1./iResolution.xy).z;
    }
    radial /= steps;

    fragColor += radial;
    fragColor = mix(fragColor,smoothstep(0.,1.,fragColor), 0.9);

    fragColor.gb *= 0.95  - smoothstep(0.,1.,dot(uvn,uvn))*0.1;

    fragColor.b *= 1. + uv.x*0.02;
    fragColor.g *= 1. + uv.t*0.05;

    fragColor = max(fragColor, 0.);
    //fragColor = pow(fragColor, vec4(0.4545 + dot(uvn,uvn)*0.1));
    fragColor *= 1. - dot(uvn,uvn)*0.9    ;
}

