#define pi acos(-1.)
#define tau (2.*pi)
#define TT (tau*t)
#define tt (tau*t)

#define init vec2 s = vec2(0)
#define ret return s
#define bpm 140.

#define tick (60./bpm)

#define beat tick
#define bar  beat*4.
#define hbar  bar/2.
#define msr  bar*4.
#define hbeat tick/2.
#define qbeat tick/4.

#define S vec2 s = vec2(0);
#define E return s;
#define T true
#define F false


bool[] pattA = bool[8](T,F,T,T,F,T,F,F);
bool[] pattS = bool[8](F,T,F,T,F,T,F,T);


bool[] pattBa = bool[8](T,T,F,T,F,T,F,T);
bool[] pattB = bool[8](T,F,T,T,F,T,F,T);
int[] notesB = int[8](0,5,2,1,0,1,3,4);

vec4[] chords = vec4[4](
    vec4(0.,3.,7.,10.),
    vec4(0. + 2.,3. + 2.,7. + 2.,10. + 2.),
    vec4(0. - 2.,3. - 2.,7. - 2.,10. - 2.),
    vec4(0.+3.,5.+3.,7.+3. - 12.,10. )
);
float note(float note, float octave){ 
	return 27.5*exp2(((octave*12. ) + note )/12.);//pow(2., );
}


vec2 hash22(vec2 p)
{
    p  = fract(p * vec2(5.3983, 5.4427));
    p += dot(p.yx, p.xy +  vec2(21.5351, 14.3137));
    return fract(vec2(p.x * p.y * 95.4337, p.x * p.y * 97.597));
}

vec2 noise(float t)
{
    return hash22(vec2(t, t * 1.423)) * 2.0 - 1.0;
}

vec2 lpnoise(float t, float fq)
{
    t *= fq;

    float ft = fract(t);
    float tn = t - ft;
    ft = smoothstep(0.0, 1.0, ft);

    vec2 n0 = noise(floor(tn + 0.0) / fq);
    vec2 n1 = noise(floor(tn + 1.0) / fq);

    return mix(n0, n1, ft);
}

vec2 kick (float t, inout float sc){
	init;


    float md = beat;
    float pl = 8.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);

    float env = exp(-t*40.7);
    s += sin((exp(-t*15.1)*200. + 40.)*tt )*env;

    s = mix(s,sin(s*3.)*1., 0.3);


    float bitreduction = 0.04;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;

    //s = sign(s)*smoothstep(0.,1.,abs(s));


    s = clamp(s,-1., 1.);


    float att = float(pattA[int(idx)]);
    s *= att;
    sc = 1. - pow(env, 0.15)*att;
    sc = smoothstep(0.,1.,sc);
    ret;
}



vec2 snare(float t){

    init;

    float md = beat;
    float pl = 8.;
    t = mod(t, md*pl);
    float idx = floor(t/md);
    t = mod(t, md);


    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 3500.)*0.45;

    float amt = 1.;
    float env = exp(-t*16.);
    //float f = (400. + exp(-t*50.)*600.)*tt;
    float f = (440./1.5 + exp(-t*50.)*1400.  )*tt;
    s += sin( f )*env;
    //s += sin( f*2.25 )*env*0.1;
    //s += sin( f*1.5 )*env*0.1;
    s += amt*sin( f + nn*0.9*smoothstep(0.,1.,t*20.) )*env;
    s -= amt*sin( f )*env;


    //s += sign(s)*pow(abs(s), vec2(2.))*0.9;
    s += amt*sin( f*4. + nn.x*exp(-t*10.))*env*env*0.2;
    //s *= 1.6;
    s *= 0.39;
    //s = mix(s,sign(s)*smoothstep(0.1,1.,abs(s)), 0.1);
	s = sin(s*1.5);

    s *= float(pattS[int(idx)]);
    ret;
}

vec2 lead(float t){
	init;

    float to;
    vec2 nn = lpnoise(t, 600.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 5500.)*0.1;


    vec2 id;
    float r = bar;
    t = mod(t, r*4.);

    id.x = floor(t/r);

    t = mod(t,bar);
    float reps = 16.;
    id.y = floor(t/(bar/reps));

    id.y = mod(id.y, 4.);

    t = mod(t,bar/reps);


    float amt = 0.4;
    float env = exp(-t*10.4);



	float n = chords[int(id.x)][int(id.y)];

    //n += floor(id.y/4.)*12.;

    s += sin( note(n, 4.) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 )*env;
    s += sin( note(n, 4.) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 + nn*0.4)*env;
    s -= sin( note(n, 4.) *tt +sin(note(n, 8.) *tt)*exp(-t*20.)*0.05 )*env;


    //s += sin(f + sin(2000.*tt)*exp(-t*20.))*env;


    float bitreduction = 0.3 + sin(6.28*to/beat)*0.0;
    s = mix(s,sign(s) * floor(abs(s)/bitreduction)*bitreduction, 0.1 + smoothstep(0.,1.,t*2.)*0.7);

    //s += sin( f )*env;
    //s += amt*sin( f + nn*0.5 )*env;
    //s -= amt*sin( f )*env;

    s *= 0.7;
    ret;
}


vec2 chord(float t){
	init;

    float r = bar;
    t = mod(t,r*4.);
    int id = int(floor(t/r));

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 4500.)*0.45;

    nn *= 0.1*sin(t);
    float m = 1.;
    for(float i = 0.; i < 5.; i++){
        float a = (1. + i/2.);
    	s += sin( note(chords[id].x, 4.) *tt*a + nn)*m;
    	s += sin( note(chords[id].y, 4.) *tt*a + nn)*m;
    	s += sin( note(chords[id].z, 4.) *tt*a + nn)*m;
    	s += sin( note(chords[id].w, 4.) *tt*a + nn)*m;
    	m *= 0.5;
    }


    s *= 0.04;
    //s = sin(s);
	ret;
}

vec2 bass(float t){
	init;

    float ttt = t;
    float r = bar;
    t = mod(t,r*4.);

    float bt = mod(t,beat*2.);
    ivec2 id = ivec2(int(floor(t/r)),int(floor(bt/(beat/4.))));

    //t = mod(t, beat*2.);

    bt = mod(bt, beat/4.);
    t = bt;
    float env = exp(-t*1.);
    float n = note(chords[id.x][id.y],1.);
    s += sin(n*tt 
             + sin(n*2.*tt)*exp(-bt*9.)*0.6
             + sin(n*4.*tt)*exp(-bt*10.)*1.

            )*env;



    s *= float(pattB[id.y]);



    float bitreduction = 0.04;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;
    //s += sin(200.*tt + exp(-t*3.1)*200.)*exp(-t*3.7)*(1. - exp(-t*1.4));


    ret;
}

vec2 hats (float t){
	init;

    t = mod(t + 0., hbeat*0.5);

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 5500.)*0.4;

    float amt = 1.;
    float env = exp(-t*15.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((9000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    s *= 0.7;
    ret;
}
vec2 hatsb (float t){
	init;

    t = mod(t + 0., hbeat/0.5);

    vec2 nn = lpnoise(t, 400.);
    nn += lpnoise(t, 500.)*0.9;
    nn += lpnoise(t, 2500.)*0.45;
    nn += lpnoise(t, 5500.)*0.4;

    float amt = 1.;
    float env = exp(-t*5.4);
    float f =  sin((12000. )*tt)*exp(-t*0.4)*0.215 +sin((9000. )*tt)*exp(-t*2.)*0.5 + ( 2000. + exp(-t*60.)*100.)*tt +lpnoise(t, 200500.).x*1.;
    s += sin( f )*env;
    s += amt*sin( f + nn )*env;
    s -= amt*sin( f )*env;

    float bitreduction = 0.35;
    s = sign(s) * floor(abs(s)/bitreduction)*bitreduction;

    s *= 0.7;
    ret;
}


vec2 mainSound( in int samp, float t )
{
    vec2 s= vec2(0);


    float sc;
    s += kick(t,sc)*1.;
    s += bass(t)*0.4*pow(sc, 1.4);
    s += snare(t);
    s += chord(t)*sc;
    s += hats(t)*0.06*(0.2 + pow(sc, 2.)*0.8)*0.4;
    s += hatsb(t)*0.06*(0.2 + pow(sc, 1.)*0.8)*0.7;
    s += lead(t)*0.4;
    s *= 0.8;

    s = mix(s,sign(s)*smoothstep(0.1,1.,abs(s)), 0.2 + sin(t)*0.2);
    s = clamp(s,-1.,1.);

    s *= smoothstep(0.,1.,t*0.5);
    return s;
}

