    for(int i=0;i<10;i++)
    {
        float f=1.*exp2(-float(i)*p);
        ao2=clamp(dist(pos+n*f)/dist(pos+n*f*fp)*fp,0.,1.);
        //ao=min(ao,ao2);
        //ao+=ao2;
        ao*=ao2/**.7+.3*/;
    }
    //ao=exp2(ao);
    fragColor.xyz=n*.3+.5;
    //fragColor.xyz=vec3(0);
    vec3 R=reflect(dir,n);
    vec4 refl=myenv(vec3(0),R,1.);
    float fres=1.-abs(dot(R,n));
    fres*=fres*fres;
    fres=.1+.7*fres;
    // not the proper way to multipy refl with ao, but looked strange with strong reflections even in wheelcase
    fragColor=mix(fragColor,refl*(ao*.35+.65),fres);
    //fragColor.xyz=vec3(.9);
    fragColor.xyz*=ao*.3+.7;
    float d=length(pos-pos0);
    fragColor=mix(fragColor,myenv(vec3(0),dir,1.),1.-exp(-d/100.));

    #ifdef WIREFRAME    
    //fragColor.y+=.5*dot(fwidth(g),fwidth(g));
    fragColor.y+=length(gl+gu-g-g)*.25;
    float tile=.2;
    vec3 frp=sin(pos/tile*PI2*.5);
    vec3 w3=fwidth(frp)*.5;
    vec3 w2=w3*w3;
    fragColor.y+=clamp(1.5-.15*length(pos),0.,1.)*.25*(exp(-frp.x*frp.x/w2.x)+exp(-frp.y*frp.y/w2.y)+exp(-frp.z*frp.z/w2.z));
    #endif

    fragColor.w=1.;
}

float circle(in vec2 _uv, in float _aspect){
    float dist = length(vec2(_uv.x * _aspect, _uv.y) - vec2(0.5 * _aspect, 0.5));
	return dist;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord )
{

    float aspect = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //get the pixel value for thisVal from buffer
    vec2 conc = texture(iChannel0, uv).xy;

    vec3 white = vec3(1.0);

    vec3 whiteness = conc.x * white;
    vec3 blackness = conc.y * white;

    vec3 color = whiteness - blackness;

    color.x += 0.125;


    float circ = smoothstep(0.675, 0.695, 1.0 - circle(uv, aspect));

    float circ2 = smoothstep(0.69, 0.695, 1.0 - circle(uv, aspect)) - smoothstep(0.70, 0.7050, 1.0 - circle(uv, aspect));

    float circ3 = smoothstep(1.0 - 0.68,1.0 - 0.67,circle(uv, aspect));


    color *= circ;
    color -= 0.2 * circ2;

    color += circ3;   



    // Output to screen
    fragColor = vec4(color, 1.0);
}

