//laplacian filter
vec2 laplacian(in vec2 uv)
{

    vec2 AB = (texture(iChannel0, uv)).xy ;
    vec2 pixel = vec2(0.001);
    vec2 laplace = -1.0 * AB;

    //slight noise added to the filter sides
    float sideWeight = 0.2;// * (0.98 + random(uv ) / 25.0 );
    float cornerWeight = 0.05;

    laplace += texture(iChannel0, uv + pixel * vec2(0, -1)).xy * cornerWeight;
    laplace += texture(iChannel0, uv + pixel * vec2(-1, 0)).xy * cornerWeight;
    laplace += texture(iChannel0, uv + pixel * vec2(1, 0)).xy * cornerWeight;
    laplace += texture(iChannel0, uv + pixel * vec2(0, 1)).xy * cornerWeight;


    laplace += texture(iChannel0, uv + pixel * vec2(-1, -1)).xy * sideWeight;
    laplace += texture(iChannel0, uv + pixel * vec2(1, -1)).xy * sideWeight;
    laplace += texture(iChannel0, uv + pixel * vec2(-1, 1)).xy * sideWeight;
    laplace += texture(iChannel0, uv + pixel * vec2(1, 1)).xy * sideWeight;

    return laplace;

}


// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float DA = 1.0;
    float DB = 0.5;
    float feed = 0.03;
    float kill = 0.062;
    float dt = 1.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float A = texture(iChannel0, uv).x ;
    float B = texture(iChannel0, uv).y ;  
    vec2 laplaceAB = laplacian(uv);
    float laplaceA = laplaceAB.x;
    float laplaceB = laplaceAB.y;


    float jitter = (random(uv * iTime) - 0.5) * 0.05 ;

    //kill *= 0.5 + random(uv);

    float nA = A + ((DA*laplaceA) - (A*B)*B + (feed*(1.0 - A)))*dt + jitter ;
    float nB = B + ((DB*laplaceB) + (A*B)*B - ((kill + feed)*B))*dt;


    //need to seed the pixels on first frame
    if(iFrame > 2) fragColor = vec4(clamp(nA, 0.0, 1.0), clamp(nB, 0.0, 1.0), 0.0, 1.0);
    else {

        float rand = noise(uv * 100.0);

        float binary = step(0.04, rand);

        fragColor = vec4(vec3(0.5 + binary/2.0, 0.5 - binary/2.0, 0.0), 1.0);
        //if(random(uv) > 0.05) fragColor = vec4(vec3(1.0, 0.0, 0.0), 1.0);
        //else fragColor = vec4(vec3(0.5, 0.5, 0.0), 1.0);   

    }
}

