////////////////////////////////////////////////////////////////////////////////
//
// Herbert Mller's method for inverse Schwarz-Christoffel mapping for
// regular polygons.
//
// Matthew Arcus, 2020.
//
// The Schwarz-Christoffel mapping:
//
// https://en.wikipedia.org/wiki/Schwarz%E2%80%93Christoffel_mapping
//
// maps the unit circle (or half-plane) to any polygon in the complex plane.
// Here we construct the inverse mapping for a regular polygon using a
// method due to Herbert Mller:
//
// http://herbert-mueller.info/uploads/3/5/2/3/35235984/circletopolygon.pdf
//
////////////////////////////////////////////////////////////////////////////////

const float PI =  3.141592654;

vec2 cpow(vec2 z, int n) {
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));
}

float binomial(float a, int n) {
   float s = 1.0;
   for (int i = n; i >= 1; i--,a--) {
     s *= float(a)/float(i);
   }
   return s;
}


vec2 expi(float x) {
  return normalize(vec2(cos(x),sin(x)));
}

vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x-z.y*w.y,
              z.x*w.y+z.y*w.x);
}

vec2 cinv(vec2 z) {
  return z*vec2(1,-1)/dot(z,z);
}

vec2 cdiv(vec2 z, vec2 w) {
  return cmul(z,cinv(w));
}

// The Lanczos approximation, should only be good for z >= 0.5,
// but we get the right answers anyway.
float gamma(float z) {
  const float[8] p = float[](
    676.5203681218851,
    -1259.1392167224028,
    771.32342877765313,
    -176.61502916214059,
    12.507343278686905,
    -0.13857109526572012,
    9.9843695780195716e-6,
    1.5056327351493116e-7
  );
  z -= 1.0;
  float x = 0.99999999999980993; // Unnecessary precision
  for (int i = 0; i < 8; i++) {
    float pval = p[i];
    x += pval/(z+float(i+1));
  }
  float t = z + 8.0 - 0.5;
  return sqrt(2.0*PI) * pow(t,z+0.5) * exp(-t) * x;
}

// The Beta function
float B(float a, float b) {
  return (gamma(a)*gamma(b))/gamma(a+b);
}

// Original Octave/Matlab code for main function:
// w=z(inZ).*( 1-cn(1)*h+(-cn(2)+(K+1)*cn(1)^2)*h.^2+
// (-cn(3)+(3*K+2)*(cn(1)*cn(2)-(K+1)/2*cn(1)^3))*h.^3+
// (-cn(4)+(2*K+1)*(2*cn(1)*cn(3)+cn(2)^2-(4*K+3)*(cn(1)^2*cn(2)-(K+1)/3*cn(1)^4)))*h.^4+
// (-cn(5)+(5*K+2)*(cn(1)*cn(4)+cn(2)*cn(3)+(5*K+3)*(-.5*cn(1)^2*cn(3)-.5*cn(1)*cn(2)^2+
//   (5*K+4)*(cn(1)^3*cn(2)/6-(K+1)*cn(1)^5/24))))*h.^5./(1+h/C^K) );

vec2 inversesc(vec2 z, int K) {

  float cn[6];
  for (int n = 1; n <= 5; n++) {
    cn[n] = binomial(float(n)-1.0+2.0/float(K),n)/float(1+n*K); // Series Coefficients
  }
  float C = B(1.0/float(K),1.0-2.0/float(K))/float(K); // Scale factor
  float T1 = -cn[1];
  float T2 = -cn[2]+float(K+1)*pow(cn[1],2.0);
  float T3 = -cn[3]+float(3*K+2)*(cn[1]*cn[2]-float(K+1)/2.0*pow(cn[1],3.0));
  float T4 = -cn[4]+float(2*K+1)*(2.0*cn[1]*cn[3]+pow(cn[2],2.0)-float(4*K+3)*
                                  (pow(cn[1],2.0)*cn[2]-float(K+1)/3.0*pow(cn[1],4.0)));
  float T5 = -cn[5]+float(5*K+2)*(cn[1]*cn[4]+cn[2]*cn[3]+float(5*K+3)*
            (-0.5*pow(cn[1],2.0)*cn[3]-0.5*cn[1]*pow(cn[2],2.0)+float(5*K+4)*
            (pow(cn[1],3.0)*cn[2]/6.0-float(K+1)*pow(cn[1],5.0)/24.0)));
  // Pixel specific code starts here
  z *= C; // Scale polygon to have diameter 1
  vec2 h = cpow(z,int(K));
  vec2 X = vec2(1,0)+h/pow(C,float(K));
  vec2 w = cmul(z,vec2(1,0) + T1*h + T2*cpow(h,2) + T3*cpow(h,3) + T4*cpow(h,4) + cdiv(T5*cpow(h,5),X));
  return w;
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

vec2 rotate(vec2 p, in float t) {
  return cmul(expi(t),p);
}

vec2 map(vec2 screenpos) {
  float scale = 1.0;
  vec2 p = (2.0*screenpos.xy-iResolution.xy)/iResolution.y;
  p *= scale;
  p = p.yx;
  return p;
}

vec3 getcolor(vec2 z, int K) {
  if (iMouse.x > 0.0) {
    vec2 c = map(iMouse.xy);
    float t = (1.0-dot(c,c))/dot(z-c,z-c);
    z = t*z-(1.0+t)*c;
    z /= dot(z,z); // Keeps chirality
  }
  // And a rotation (also a Mobius transformation)
  z = rotate(z,0.5*iTime);

  float r = length(z);
  float px = 0.5*fwidth(z.x);
  float theta = atan(z.y,z.x);
  vec3 col = hsv2rgb(theta/(2.0*PI),1.0,1.0);
  float A = 4.0;
  float B = 2.0*float(K);
  float a = -log(r)*A;
  float ds = 0.07;
  if (a <= 6.0+ds) {
    a = fract(a);
    float b = fract(theta/PI*B);
    float d = min(min(a,1.0-a),min(b,1.0-b));
    col *= mix(0.2,1.0,smoothstep(-ds-px,ds+px,d));
  }
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 z = map(fragCoord.xy);
  vec3 col = vec3(0.5);
  int K = (3+int(0.2*iTime)%7);
  // Check if point in polygon
  bool inpoly = true;
  {
    float theta = PI/float(K);
    vec2 n = vec2(cos(theta),sin(theta));
    for (int i = 0; i < K; i++) {
      vec2 w = rotate(z,2.0*float(i)*theta);
      w.x -= 1.0;
      if (dot(w,n) > 0.0) inpoly = false;
    }
  }
  bool incircle = length(z) < 1.0;
  z = inversesc(z,K);
  col = getcolor(z,K);
  if (!incircle) col *= 0.75;
  if (!inpoly) col *= 0.75;
  fragColor = vec4(col,1);
}

float pi = acos(-1.);
float glo = 0.;

mat2 rot(float rad)
{
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

vec2 hexPris(vec3 p, vec2 h, float matId)
{
	const vec3 k = vec3(-.8660254, .5, .57735);
    p = abs(p);
    p.xy -= 2.*min(dot(k.xy, p.xy), 0.)*k.xy;
    vec2 d = vec2(length(p.xy - vec2(clamp(p.x, -k.z*h.x, k.z*h.x), h.x))*sign(p.y - h.x), p.z - h.y);
    return vec2(min(max(d.x,d.y), 0.) + length(max(d, 0.)), matId);
}

vec3 kif(vec3 p)
{
    for(int i = 0; i < 6; i++)
    {
        p = abs(p) - vec3(.05*cos((iTime + 76.)*.21) + .31,
                          -.05*sin((iTime + 287.)*.23) + .35,
                          .05*sin((iTime + 150.)*.25) + .37);
        p.xz *= rot(.031*(iTime + 483.));
        p.zy *= rot(.043*(iTime + 985.));
        p.xy *= rot(.053*(iTime + 115.));
    }

    return p;
}

vec2 map(vec3 p)
{   
    p = kif(p);

    float len = 20.;
    p.z -= len/2.;
    vec2 m1 = hexPris(p, vec2(.025, len), 1.);
    p.x -= .03;
    vec2 m2 = hexPris(p, vec2(.01, len), 2.);
    vec2 m = m1.x < m2.x ? m1 : m2;
    glo += .1 / (.1 + m.x*m.x*m.x*2500.);
    return m;
}

vec2 tr(vec3 ro, vec3 rd)
{
    float far = 40.;
	vec2 h,t= vec2(.001);
	for(int i = 0; i < 384; i++)
	{
		h = map(ro + rd*t.x);
		if(h.x < .00001 || t.x > far)
			break;
		t.x += h.x;
        t.y = h.y;
  	}
    if(t.x > far)
        t.y = 0.;
	return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 ro = vec3(1.5*cos(-.2*iTime),
                   1.5*sin(-.2*iTime),
                   -3.5);
	vec3 camTarget = vec3(0.);
	vec3 up = vec3(0., 1., 0.);
	vec3 camDir = normalize(camTarget - ro);
	vec3 camRight = normalize(cross(up, ro));
	vec3 camUp = normalize(cross(camDir, camRight));
    vec3 lightPos = vec3(.5, 1.5, -15.);

	vec2 screenPos = -1. + 2. * gl_FragCoord.xy / iResolution.xy;
	screenPos.x *= iResolution.x / iResolution.y;

	vec2 eps = vec2(0., .01);
	vec3 rd = normalize(camRight*screenPos.x + camUp*screenPos.y + camDir);

	vec2 t = tr(ro, rd);

    vec3 colRot = vec3(sin(.317*(iTime + 44.)),
                       sin(.151*(iTime + 55.)),
                       sin(.227*(iTime + 79.))) + 1.25;

    if (t.y > 0.)
    {
        vec3 hit = ro + rd*t.x;
        vec3 lightDir = normalize(lightPos - hit);

        vec3 norm = normalize(map(hit).x - vec3(map(hit - eps.yxx).x,
                              					map(hit - eps.xyx).x,
                              					map(hit - eps.xxy).x));

        float diff = max(0., dot(lightDir, norm));
        float spec = pow(max(dot(rd, reflect(norm, lightDir)), 0.), 85.);
        float ao = clamp(map(t.x + norm*.5).x / .5, 0., 1.);

        vec3 col = .23 * colRot;
        col *= .1 * ao;
        col += .5 * diff * colRot.yzx;
        col += .8 * spec * vec3(1., 1., 1.);

        col += glo*.008*colRot;

        if(t.y == 2.)
            col = col.zxy;

        fragColor = vec4(col, 1.);
    }
    else
    	fragColor = vec4(glo*.008*colRot.zyx, 1.);
}

