#define AA  0   // optional antialiasing

#define VAR_ZERO min (iFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrEllipsDf (vec3 p, vec3 r);
float SmoothMin (float a, float b, float r);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float SmoothBump (float lo, float hi, float w, float x);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
void PxInit ();
void PxBgn (vec2 p);
float PxText (vec2 p, int txt);
float PxChar (vec2 p, vec4 c);

mat3 vuMat;
vec3 ltDir, pPos;
vec2 sSize, cSpace;
float dstFar, tCur, sMidHt, fAng, szFac, pRot;
int idObj, nRec;
const int nRecMax = 5;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float TuxDf (vec3 p, float dMin)
{
  vec3 q;
  float d, dh;
  p /= szFac;
  dMin /= szFac;
  q = p;
  d = PrEllipsDf (q.xzy, vec3 (1.3, 1.2, 1.4));
  q.y -= 1.5;
  dh = PrEllipsDf (q.xzy, vec3 (0.8, 0.6, 1.3));
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.3, 2., -0.4);
  d = SmoothMin (d, max (dh, - PrCylDf (q, 0.15, 0.3)), 0.2);
  DMIN (11);
  q = p;
  q.yz -= vec2 (1.6, -0.6);
  d = max (PrEllipsDf (q, vec3 (0.4, 0.2, 0.6)), 0.01 - abs (q.y));
  DMIN (12);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.3, 2., -0.4);
  d = PrSphDf (q, 0.15);
  DMIN (13);
  q = p;
  q.x = abs (q.x);
  q.xy -= vec2 (0.6, -1.05);
  q.yz = Rot2D (q.yz, -0.5 * pi);
  q.y -= -0.6;
  d = PrCylDf (q.xzy, 0.12, 0.7);
  DMIN (14);
  q -= vec3 (0.1, -0.67, -0.4);
  q.xz = Rot2D (q.xz, -0.07 * pi);
  d = PrEllipsDf (q.xzy, vec3 (0.15, 0.5, 0.05));
  q.z -= 0.5;
  q.xz = Rot2D (q.xz, 0.15 * pi);
  q.z -= -0.5;
  d = SmoothMin (d, PrEllipsDf (q.xzy, vec3 (0.15, 0.5, 0.05)), 0.05);
  q.z -= 0.5;
  q.xz = Rot2D (q.xz, -0.3 * pi);
  q.z -= -0.5;
  d = SmoothMin (d, PrEllipsDf (q.xzy, vec3 (0.15, 0.5, 0.05)), 0.05);
  DMIN (15);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (1.1, 0.3, -0.2);
  q.yz = Rot2D (q.yz, -0.25 * pi);
  q.xy = Rot2D (q.xy, fAng) - vec2 (0.1, -0.4);
  d = PrEllipsDf (q.xzy, vec3 (0.05, 0.25, 0.9));
  DMIN (16);
  return dMin * szFac;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, h;
  dMin = dstFar;
  q = p;
  q.y -= 0.35;
  d = PrRoundBoxDf (q, vec3 (1., 0.16, 0.7), 0.01);
  q = p - vec3 (0.7, 0.4, -0.72);
  d = max (d, - PrBoxDf (q, vec3 (0.17, 0.01, 0.2)));
  q.y -= -0.02;
  d = max (d, - PrBoxDf (q, vec3 (0.08, 0.08, 0.03)));
  q.y -= 0.01;
  d = max (d, - PrBoxDf (q, vec3 (0.045, 0.05, 0.05)));
  q = p;
  q.yz -= vec2 (0.3, -0.72);
  d = max (d, - PrBoxDf (q, vec3 (1.1, 0.01, 0.03)));
  q = p;
  q.x = abs (q.x) - 0.4;
  q.yz -= vec2 (0.35, -0.72);
  d = max (d, - PrBoxDf (q, vec3 (0.01, 0.17, 0.03)));
  q = p;
  q.x = abs (q.x) - 1.;
  q.yz -= vec2 (0.35, 0.05);
  d = max (d, - PrBoxDf (q, vec3 (0.02, 0.007, 0.7)));
  DMIN (1);
  q = p;
  q.x = abs (q.x) - 0.95;
  q.y -= 0.09;
  q.x -= 0.02 * q.y;
  d = PrRoundBoxDf (q, vec3 (0.025 + 0.02 * q.y, 0.09, 0.6), 0.005);
  q = p;
  q -= vec3 (-0.05, 0.09, 0.3);
  d = min (d, PrRoundBoxDf (q, vec3 (0.9, 0.09, 0.02), 0.005));
  DMIN (2);
  q = p;
  q.yz -= vec2 (0.55, 0.05);
  q.xz = abs (q.xz) - vec2 (0.55, 0.4);
  d = PrCylDf (q.xzy, 0.05, 0.05);
  DMIN (3);
  q = p;
  q.yz -= vec2 (0.71 + sSize.y, -0.45);
  d = 0.9 * max (PrRoundBoxDf (q, vec3 (sSize.x + 0.16, sSize.y + 0.12, 0.13), 0.03),
     - PrRoundBox2Df (q.xy, sSize + 0.01, 0.03));
  DMIN (4);
  q = p - vec3 (0.7, 0.4, -0.55);
  d = PrBoxDf (q, vec3 (0.16, 0.008, 0.16));
  DMIN (5);
  q = p;
  h = sSize.y + 0.05 - 0.1 * (q.z - 0.05);
  q.yz -= vec2 (0.6 + h, 0.05);
  d = 0.9 * PrRoundBoxDf (q, vec3 (sSize.x + 0.12, h, 0.6), 0.03);
  DMIN (6);
  q = p - pPos;
  q.xz = Rot2D (q.xz, pRot);
  dMin = TuxDf (q, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjAO (vec3 ro, vec3 rd)
{
  float ao, d;
  ao = 0.;
  for (int j = VAR_ZERO; j < 8; j ++) {
    d = float (j + 1) / 16.;
    ao += max (0., d - 3. * ObjDf (ro + d * rd));
  }
  return 0.5 + 0.5 * clamp (1. - 0.2 * ao, 0., 1.);
}

float ChqPat (vec2 p)
{
  vec2 ip;
  ip = floor (p);
  if (2. * floor (ip.y / 2.) != ip.y) p.x += 0.5;
  p = smoothstep (0., 0.1, abs (fract (p + 0.5) - 0.5));
  return 0.98 + 0.02 * p.x * p.y;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col;
  vec2 q;
  float d;
  if (rd.y >= 0.) col = vec3 (0.2, 0.3, 0.6) + 0.2 * pow (1. - rd.y, 5.);
  else {
    d = - ro.y / rd.y;
    q = ro.xz + d * rd.xz;
    col = mix (ChqPat (2. * q) * vec3 (0.8, 0.8, 0.9) * (0.8 + 0.2 * Fbm2 (128. * q)),
       vec3 (0.4, 0.5, 0.8), 1. - min (1., exp2 (2. - 8. * d / dstFar)));
    col *= 0.9 + 0.1 * smoothstep (0.25, 0.4, length (q - pPos.xz));
  }
  return col;
}

vec3 ShowScene (vec3 ro)
{
  vec4 col4;
  vec3 col, rd, roh, vn, q;
  vec2 sb;
  float dstObj, ao;
  bool isBg, isScrn;
  isBg = false;
  isScrn = true;
  for (int k = VAR_ZERO; k <= nRecMax; k ++) {
    rd = normalize (vec3 (ro.xy, 7.));
    if (k == 0) ro.xy = vec2 (0.);
    ro.z -= 20. * sSize.y;
    rd = vuMat * rd;
    ro = vuMat * ro;
    ro.y += sMidHt;
    dstObj = ObjRay (ro, rd);
    if (dstObj >= dstFar) isBg = true;
    else {
      ro += dstObj * rd;
      roh = ro;
      if (idObj != 6) isScrn = false;
      else {
        ro.y -= sMidHt;
        sb = abs (ro.xy) - sSize;
        isScrn = (max (sb.x, sb.y) < 0. && ro.z < -0.5 && k < nRec);
      } 
    }
    if (! isScrn || isBg || k == nRec) break;
  }
  if (isBg) {
    col = BgCol (ro, rd);
    if (rd.y < 0.) {
      roh = ro - (ro.y / rd.y) * rd;
      if (length (roh.xz) < 2.) col *= ObjAO (roh, vec3 (0., 1., 0.));
    }
  } else {
    col4 = vec4 (0.8, 0.81, 0.79, 0.);
    PxInit ();
    if (idObj < 11) {
      if (idObj == 1) {
        if (ro.z < -0.65) {
          col4.rgb *= 1.2;
          sb = ro.xy - vec2 (-0.82, 0.42);
          sb.x -= 0.2 * sb.y;
          if (PxText (sb * 260., 1) > 0.) col4 = vec4 (0.1);
          else if (PxText ((ro.xy - vec2 (-0.75, 0.21)) * 240., 2) > 0.)
             col4 = vec4 (0.1, 0.1, 0.8, 0.3);
          else if (abs (abs (ro.x) - 0.9) < 0.02 && abs (ro.y - 0.3) < 0.008)
             col4 = (ro.x < 0.) ? vec4 (0., 1., 0., -1.) : ((mod (0.33 * tCur, 1.) < 0.3) ?
             vec4 (1., 0., 0., -1.) : vec4 (0.4, 0.4, 0.4, 0.1));
        }
      } else if (idObj == 2) {
        col4 *= 0.9;
      } else if (idObj == 3) {
        col4.rgb = vec3 (0.4, 0.3, 0.1);
      } else if (idObj == 4) {
        if (ro.z < -0.45) col4 *= 1.2;
        if (ro.z < -0.6 && abs (ro.x) < sSize.x + 0.15 &&
           abs (ro.y - sMidHt) < sSize.y + 0.11) col4 *= 0.8;
      } else if (idObj == 5) {
        col4.rgb = vec3 (0.1);
      } else if (idObj == 6) {
        if (length (ro.yz - vec2 (-0.4, -0.05)) < 0.15 &&
           mod (ro.y + 0.4 - 0.01, 0.04) < 0.02) col4 *= 0.4;
        else if (ro.z < -0.5) {
          col4 = vec4 (0.1);
          sb = (ro.xy - vec2 (0., 0.6 * sSize.y)) * 340.;
          if (PxText (sb, 0) > 0.) col4 = vec4 (1., 0., 0., -1.);
          else if (mod (tCur, 1.) < 0.5) {
            ro.xy = (abs (sb - vec2 (0., -1.5) * cSpace) - vec2 (29., 7.) * cSpace);
            if (max (ro.x, ro.y) < 1.25 &&
               (min (abs (ro.x), abs (ro.y)) < 1.25)) col4 = vec4 (1., 0., 0., -1.);
          }
        }
      }
    } else {
      if (idObj == 11) {
        q = ro - pPos; 
        q.xz = Rot2D (q.xz, pRot);
        col4 = (q.z < -0.6 * szFac || q.z < 0. && length (vec2 (abs (q.x) - 0.3 * szFac,
           q.y - 2. * szFac)) < 0.2 * szFac) ? vec4 (0.9, 0.9, 0.9, 0.2) : vec4 (0.1, 0.1, 0.3, 0.1);
      } else if (idObj == 12) col4 = vec4 (1., 0.8, 0.2, 0.2);
      else if (idObj == 13) col4 = vec4 (0., 0.3, 0., 0.2);
      else if (idObj == 14) col4 = vec4 (0.8, 0.8, 0., 0.1);
      else if (idObj == 15) col4 = vec4 (0.9, 0.9, 0., 0.1);
      else if (idObj == 16) col4 = vec4 (0.15, 0.15, 0.2, 0.1);
    }
    if (! isScrn) {
      if (col4.a >= 0.) {
        vn = ObjNf (roh);
        ao = ObjAO (roh, vn);
        if (idObj < 11) vn = VaryNf (128. * roh, vn, 0.2);
        col = col4.rgb * (0.2 + 0.1 * max (0., - dot (vn, normalize (vec3 (ltDir.xz, 0.).xzy))) +
           0.8 * ao * (max (0., dot (vn, ltDir)))) +
           ao * col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
      } else col = col4.rgb;
    }
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ut;
  float el, az, zmFac, sr, ds;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  ut = abs (uv) - vec2 (1.33, 1.);
  if (max (ut.x, ut.y) > 0.003) col = vec3 (0.82);
  else {
    dstFar = 60.;
    ltDir = normalize (vec3 (1., 3., -1.));
    ds = SmoothBump (0.2, 0.8, 0.1, mod (0.043 * tCur, 1.));
    az = -0.15 * pi * (2. * floor (mod (0.043 * tCur + 0.5, 2.)) - 1.) * (1. - ds);
    el = -0.01 * pi * (1. - ds);
    if (mPtr.z > 0.) {
      az += 2. * pi * mPtr.x;
      el += 0.7 * pi * mPtr.y;
    }
    az = clamp (az, -0.4 * pi, 0.4 * pi);
    vuMat = StdVuMat (clamp (el, -0.4 * pi, 0.01 * pi), az);
    sSize = 0.55 * vec2 (1.33, 1.);
    sMidHt = 1.26;
    pPos = vec3 (0.3, 0.35, -2.);
    pRot = 1.3 * az;
    szFac = 0.25;
    fAng = -0.2 * pi + 0.1 * pi * SmoothBump (0.25, 0.75, 0.1, mod (0.2 * tCur, 1.)) *
       sin (16. * pi * tCur);
