#define PI 3.1415926535897932384626433832795
#define TWO_PI 6.283185307179586476925286766559

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.x;     
    //0.02 - 0.4


    float stp = smoothstep(0.0, 1.0, mod(iTime / 20.0 , 2.0));
    float dis = (1.0 - stp) * 0.38 + 0.009;


   //if (dis < 0.01) dis = 0.01;


    float currentDegree = TWO_PI * mod(iTime/4.0, 1.0);
    float x = cos(currentDegree) * dis;
    float y = sin(currentDegree) * dis;

    //0.7 - 20.0
    stp = smoothstep(0.0, 1.0, mod(iTime / 40.0 , 1.0));
    float lght = stp * 20.3 + 0.7;
    float pct = pow(distance(st,vec2(0.4)), distance(st,vec2(x + 0.4, y + 0.4))*lght);


    vec3 color = vec3(pct);
    float m = smoothstep(0.0, 0.01, stp);
	color = vec3(mix(1.0, pct, m));
    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;

    vec2 st = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;

    // Scale
    st *= 30.0;

    // Tile the space
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);


    vec2 point = random(i_st);


    float m_dist = 0.80;  // minimum distance

    //loop over neighbour for nearest

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = random(i_st + neighbor);

			// Animate the point
            point = 0.5 + 0.5*sin(iTime/1.5 + 6.99999 * point);

			// Vector between the pixel and the point
            vec2 diff = neighbor + point - f_st;

            // Distance to the point
            float dist = length(diff);

            // Keep the closer distance
            m_dist = min(m_dist, sqrt(dist));
        }
    }

    //tweak lighting

    m_dist += 0.1;

    //urgh at this hard coding

    float circ = smoothstep(0.675, 0.695, 1.0 - circle(uv, aspect));

    float circ2 = smoothstep(0.69, 0.695, 1.0 - circle(uv, aspect)) - smoothstep(0.70, 0.7050, 1.0 - circle(uv, aspect));

    float circ3 = smoothstep(1.0 - 0.68,1.0 - 0.67,circle(uv, aspect));


    vec3 color = vec3(m_dist + 0.05,m_dist, m_dist);


    color *= circ;
    color -= 0.2 * circ2;

    color += circ3;   

    fragColor = vec4(color,1.0);
}

