uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
uint hash( uvec2 v ) { 
    return hash( v.x ^ hash(v.y)); 
}

float floatConstruct( uint m ) {
    const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint ieeeOne      = 0x3F800000u; // 1.0 in IEEE binary32

    m &= ieeeMantissa;                     // Keep only mantissa bits (fractional part)
    m |= ieeeOne;                          // Add fractional part to 1.0

    float  f = uintBitsToFloat( m );       // Range [1:2]
    return f - 1.0;                        // Range [0:1]
}
float random( vec2  v ) { 
    return floatConstruct(hash(floatBitsToUint(v)));
}
mat2 rot(float a){
	float s = sin(a);
    float c = cos(a);

    return mat2(c, -s, s, c);
}

float star(vec2 uv, float flar){
	float d = length(uv);
    float m = .05/d;
    m *= m;

    float ray = max(0., 1.-abs(uv.x*uv.y*5000.));
    m += ray*flar;

    uv *= rot(atan(1.));
    ray = max(0., 1.-abs(uv.x*uv.y*5000.));
    m += ray*flar;

    m *= smoothstep(1., .2, d);

    return m;
}
vec3 starLayer(vec2 uv){
	vec2 gv = fract(uv);
    vec2 id = floor(uv);
    vec3 col = vec3(0);

    for(float x = -1.; x <= 1.; x++){
        for(float y = -1.; y <= 1.; y++){
            vec2 off = vec2(x, y);
	    	vec2 roff = vec2(random(id+off), random(id+off+vec2(243.23, 882.23)));
            float size = fract(random(id+off)*68321.7829);
    		float star = star(gv-off-roff, smoothstep(.7, 1., size)*.5);

            vec3 color = sin(vec3(.2, .3, .8)*fract(size*3219.329)*214767.)*.5+.5;
            color *= vec3(1., .5, 1.+size);
            col += star*size*size*color;

        }
    }

    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
   	float t = iTime*.1;
    vec3 col = vec3(0);
    float maxLayer = 7.;

    uv *= rot(t);
    for(float i = 0.; i < 1.; i += 1./maxLayer){
        float depth = fract(i+t);

        float scale= mix(10., 5., depth);
        float fade = depth*smoothstep(1., .8, depth);
    	col += starLayer(uv*scale+i*274.23)*fade;	
    }





    //if(gv.x > .98 || gv.y > .98) col = vec3(1., 0., 0.);
    fragColor = vec4(col,1.0);
}

// template copied form inigo quilez - iq/2019

//   Step 1: https://www.shadertoy.com/view/ws3GD2


#define AA 2

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sdSphere( in vec3 p, in float r )
{
    return length(p)-r;
}

float sdOct(in vec3 p){
    //vec3 n = normalize(vec3(1. / 3.));
    return dot(vec3(0.57735), abs(p)) - 0.057735;
    //return dot(vec3(1.), p) - r;
}

float sdTransOct(in vec3 p, in float a, in float b, in float h){
    vec3 n = normalize(cross(vec3(a, 0, 0) - vec3(0, 0, b), vec3(0, h, 0) - vec3(0, 0, b)));
    return dot(n, abs(p)) - a * n.x;
}

float sdKunai(in vec3 p, in float fl, in float bl, in float w, in float h, in float sh){
    p.xy = abs(p.xy);
    vec3 A = vec3(0, h, sh);
    vec3 B = vec3(0, 0, fl);
    vec3 C = vec3(w, 0, 0);
    vec3 n = normalize(cross(B - A, C - A));
    //float d1 = dot(n, p) - fl * n.z;
    //float d1 = (p.z < fl) ? dot(n, p) - fl * n.z : 0.;
    float d1 = dot(n, p) - fl * n.z;
    vec3 D = vec3(0, 0, -bl);
    n = normalize(cross(C - A, D - A));
    //d1 = (p.z > -bl) ? max(d1, dot(n, p) + bl * n.z) : d1;
    d1 = max(d1, dot(n, p) + bl * n.z);
    d1 = (p.z < sh) ? mix(smin(d1, length(p.xy), 0.04), d1, p.z - sh) : d1;
    return d1;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdKunaiHandle(in vec3 p, in float k, in float w, in float r){
    p.yz = p.zy;
    float y = p.y;
    p.y = (y>0.&&y< k * w)?fract(p.y / w) * w - w / 2.:y - w / 2.;
    p.y = (y>k * w)?y-k * w - w / 2.:p.y;
    //if (y < 0.) return y;
    return sdTorus(p, vec2(r, w));
}

//float sdTransOct(in vec3 p, in vec3

vec4 map( in vec3 p, float time )
{
    //float d = sdOct( p * 1.1) / 1.1;
    //float d = sdTransOct(p, 0.1, 0.3, 0.1);
    //float d = sdKunai(p, 0.4, 0.1, 0.1, 0.03, 0.05);
    //float d = sdTorus(p, vec2(0.2, 0.1));
    float d = sdKunaiHandle(-p + vec3(0, 0, 0.01), 20., 0.01, 0.02);
    d = min(d, sdKunai(p + vec3(0, 0, -0.1), 0.4, 0.1, 0.1, 0.03, 0.05));
    //d = min(d, sdKunai(p + vec3(0, 0, -0.1), 0.4, 0.1, 0.1, 0.03, 0.05));
    d = min(d, length(p.xy));
    //float d = sdSphere( p, 0.1 );
    return vec4( d, p );
}

#define ZERO min(iFrame,0)

vec3 calcNormal( in vec3 pos, in float time )
{
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e,time).x;
    }
    return normalize(n);
}

float calcAO( in vec3 pos, in vec3 nor, in float time )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos+h*nor, time ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

float calcSoftshadow( in vec3 ro, in vec3 rd, in float k, in float time )
{
    float res = 1.0;

    float tmax = 2.0;
    float t    = 0.001;
    for( int i=0; i<64; i++ )
    {
        float h = map( ro + rd*t, time ).x;
        res = min( res, k*h/t );
        t += clamp( h, 0.012, 0.2 );
        if( res<0.001 || t>tmax ) break;
    }

    return clamp( res, 0.0, 1.0 );
}

vec4 intersect( in vec3 ro, in vec3 rd, in float time )
{
    vec4 res = vec4(-1.0);

    float t = 0.001;
    float tmax = 10.0;
    for( int i=0; i<128 && t<tmax; i++ )
    {
        vec4 h = map(ro+t*rd,time);
        if( h.x<0.0001 ) { res=vec4(t,h.yzw); break; }
        t += h.x;
    }

    return res;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        float d = 0.5*sin(fragCoord.x*147.0)*sin(fragCoord.y*131.0);
        float time = iTime;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        float time = iTime;
        #endif

	    // camera	
        float an = 6.2831*time/30.0;
        vec3 ta = vec3( 0.0, 0.0, 0.0 );
        vec3 ro = ta + vec3( 0.5*cos(an), 0.5, 0.5*sin(an) );

        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );

        // ray direction
        float fl = 2.0;
        vec3 rd = ca * normalize( vec3(p,fl) );

        // background
        vec3 col = vec3(1.0+rd.y)*0.03;

        // raymarch geometry
        vec4 tuvw = intersect( ro, rd, time );
        if( tuvw.x>0.0 )
        {
            // shading/lighting	
            vec3 pos = ro + tuvw.x*rd;
            vec3 nor = calcNormal(pos, time);

            col = 0.5 + 0.5*nor;
        }


        // gamma        
	    tot += pow(col,vec3(0.45) );
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // cheap dithering
    tot += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

    fragColor = vec4( tot, 1.0 );
}

////////////////////////////////////////////////////////////////////////////////
//
// The Nine Point Conic
// Matthew Arcus, 2020.
//
// Given a triangle ABC and a point P, can draw a conic between:
// The midpoints of the triangle sides
// The midpoints of the lines from P to each vertex
// The points where these lines cut the sides of the triangle
//
// Mouse controls position of P.
//
// First described by Maxime Bcher in 1892.
//
// The conic changes from ellipse to hyperbola when it crosses one of
// the extended triangle sides, and becomes a circles when P coincides
// with the orthocentre (intersection of altitudes - the cyan lines).
//
////////////////////////////////////////////////////////////////////////////////

////////////////////////////////////////////////////////////////////////////////
//
// Geometric constructions
//
// Homogeneous coordinates used throughout (so the lines and points are
// "projective"):
// (x,y,z) represents the point (x/z,y/z) for z != 0
// (x,y,0) represents a "point at infinity" which acts like a direction.
// (a,b,c) represents the line ax + by + cz = 0
// (0,0,c) represents the "line at infinity"
//
////////////////////////////////////////////////////////////////////////////////

// The line between two projective points, or the intersection of two
// projective lines, is just their cross product.
vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// The midpoint between p and q (also conjugate with point at infinity)
vec3 midpoint(vec3 p, vec3 q) {
  return p*q.z + p.z*q;
}

float distance2(vec3 p, vec3 q) {
  p /= p.z; q /= q.z;
  return abs(dot(p-q,p-q));
}

// Perpendicular bisector (a line)
vec3 bisector(vec3 p,vec3 q) {
  // (p-r).(p-r) = (q-r).(q-r) // r equidistant from p and q
  // pp - 2pr + rr = qq - 2qr + rr
  // pp - qq = 2pr - 2qr
  // 2r.(p-q) = pp - qq
  p /= p.z; q /= q.z;
  return vec3(2.0*(p.xy-q.xy),dot(q,q)-dot(p,p));
}

// Drop perpendicular from p onto line q.
vec3 perpendicular(vec3 p, vec3 q) {
  // (p + kq.xy).q = 0
  // p.q + k(q.yx).q = 0
  float k = dot(p,q)/dot(q.xy,q.xy);
  return p - k*vec3(q.xy,0);
}

// Find a projective mapping taking p0,p1,p2,p4 to
// triangle of reference and unit point, ie:
// p0 -> (1,0,0), p1 -> (0,1,0), p2 -> (0,0,1), p3 -> (1,1,1)
// No three points collinear.
mat3 rproject(vec3 p0, vec3 p1, vec3 p2, vec3 p3) {
  // Just an inverse for the first three points
  // (the triangle of reference). No inverse if collinear.
  mat3 m = inverse(mat3(p0,p1,p2)); // column major!
  vec3 p3a = m*p3;
  // Then scale each row so the unit point (1,1,1) is correct
  m = transpose(m);
  // zero components here only if not collinear
  m[0] /= p3a[0];
  m[1] /= p3a[1];
  m[2] /= p3a[2];
  m = transpose(m);
  return m;
}

// Construct the conic defined by 5 points.
// Method taken from "Geometry", Brannan, Esplan & Gray, CUP, 2012
mat3 solve(vec3 p0, vec3 p1, vec3 p2, vec3 p3, vec3 p4) {
  // p takes p0,p1,p2,p3 to triangle of reference and unit point
  mat3 p = rproject(p0,p1,p2,p3);
  // Now construct a conic through the images of p0-p4,
  vec3 p4a = p*p4;
  float a = p4a.x, b = p4a.y, c = p4a.z;
  float d = c*(a-b);
  float e = b*(c-a);
  float f = a*(b-c);
  mat3 m = mat3(0,d,e,
                d,0,f,
                e,f,0);
  // And combine the two.
  mat3 res = transpose(p)*m*p;
  return res;
}

////////////////////////////////////////////////////////////////////////////////
// Distance functions
////////////////////////////////////////////////////////////////////////////////

float point(vec3 p, vec3 q) {
  float pz = p.z, qz = q.z;
  p *= qz; q *= pz;
  return distance(p,q)/abs(p.z);
}

float line(vec3 p, vec3 q) {
  return abs(dot(p,q)/(p.z*length(q.xy)));
}

float line(vec3 p, vec3 q, vec3 r) {
  return line(p,join(q,r));
}

float segment(vec3 p, vec3 q, vec3 r) {
  p /= p.z; q /= q.z; r /= r.z; // normalize
  vec3 pa = p-q;
  vec3 ba = r-q;
  float h = clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0);
  float d = length(pa-h*ba);
  return d;
}

float conic(vec3 p, mat3 m) {
  float s = dot(p,m*p);
  vec3 ds = 2.0*m*p; // Gradient
  return abs(s/(p.z*length(ds.xy))); // Normalize for Euclidean distance
}

float circle(vec3 p, vec4 c) {
  // (x-a)^2 + (y-b)^2 = r^2
  // x2 -2ax + a2 + y2 -2by + b2 -r2 = 0
  vec3 q = c.xyz;
  float r2 = c.w;
  q /= q.z;
  return conic(p,mat3(1,0,-q.x,
                      0,1,-q.y,
                     -q.x,-q.y,dot(q.xy,q.xy)-r2));
}

float quad(vec3 p, vec3 a, vec3 b, vec3 c, vec3 d) {
  float t = 1e8;
  t = min(t,segment(p,a,b));
  t = min(t,segment(p,b,c));
  t = min(t,segment(p,c,d));
  t = min(t,segment(p,d,a));
  return t;
}

////////////////////////////////////////////////////////////////////////////////
// Drawing functions
////////////////////////////////////////////////////////////////////////////////

void getmin(inout float d, inout int index, int i, float t) {
  if (t < d) { d = t; index = i; }
}

vec3 colors[] =
  vec3[](vec3(1,0,0),
         vec3(1,1,0),
         vec3(0,0,1),
         vec3(0,0.8,0),
         vec3(0,1,1),
         vec3(0.8),
         vec3(1,0,1),
         vec3(1,1,1)
         );

vec3 getcol(int i) {
  return colors[i];
}

vec3 diagram(vec3 p, vec3 P) {
  vec3 pcol = vec3(0.3);
  float lwidth = 0.005;
  float dwidth = 1.5*fwidth(p.x/p.z);
  float pwidth = 0.03;
  // The triangle (P is parameter)
  vec3 A = vec3(1.0,1.0,1), B = vec3(-1.5,-0.5,1), C = vec3(1,-1,1);
  vec3 AB = join(A,B), BC = join(B,C), CA = join(C,A);
  vec3 PA = join(P,A), PB = join(P,B), PC = join(P,C);
  vec3 mBC = midpoint(B,C), mCA = midpoint(C,A), mAB = midpoint(A,B);
  vec3 mPA = midpoint(P,A), mPB = midpoint(P,B), mPC = midpoint(P,C);
  vec3 aA = join(PA,BC), aB = join(PB,CA), aC = join(PC,AB);
  vec3 pA = perpendicular(A,BC);
  vec3 pB = perpendicular(B,CA);
  vec3 pC = perpendicular(C,AB);
  vec3 H = join(join(A,pA),join(B,pB));
  mat3 X = solve(mAB,mBC,mCA,mPA,mPB);

  vec3 bA = bisector(A,B);
  vec3 bB = bisector(B,C);
  // Intersect at the circumcentre
  vec3 O = join(bA,bB);
  // centre of the circumcircle.
  vec4 C1 = vec4(O,distance2(O,A));

  float d = 1e8;
  int index = -1;
  getmin(d,index,0,line(p,AB));
  getmin(d,index,0,line(p,BC));
  getmin(d,index,0,line(p,CA));

  getmin(d,index,1,line(p,PA));
  getmin(d,index,1,line(p,PB));
  getmin(d,index,1,line(p,PC));

  getmin(d,index,4,line(p,A,pA));
  getmin(d,index,4,line(p,B,pB));
  getmin(d,index,4,line(p,C,pC));

  if (false) {
    getmin(d,index,3,quad(p,mAB,mCA,mPC,mPB));
    getmin(d,index,3,quad(p,mBC,mAB,mPA,mPC));
    getmin(d,index,3,quad(p,mCA,mBC,mPB,mPA));
  }
  getmin(d,index,2,conic(p,X));
  getmin(d,index,5,circle(p,C1));

  vec3 col = vec3(1,1,0.8);  
  col = mix(0.9*getcol(index),col,smoothstep(lwidth,lwidth+dwidth,d));
  d = 1e8;
  getmin(d,index,0,point(p,P));

  getmin(d,index,0,point(p,A));
  getmin(d,index,0,point(p,B));
  getmin(d,index,0,point(p,C));

  getmin(d,index,0,point(p,mBC));
  getmin(d,index,0,point(p,mCA));
  getmin(d,index,0,point(p,mAB));

  getmin(d,index,0,point(p,mPA));
  getmin(d,index,0,point(p,mPB));
  getmin(d,index,0,point(p,mPC));

  getmin(d,index,0,point(p,aA));
  getmin(d,index,0,point(p,aB));
  getmin(d,index,0,point(p,aC));

  getmin(d,index,0,point(p,H));

  col = mix(pcol,col,smoothstep(pwidth,pwidth+dwidth,d));
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float scale = 2.0;
  vec3 p = vec3(scale*(2.0*fragCoord-iResolution.xy)/iResolution.y,1.0);
  vec3 P = iMouse.x <= 0.0 ? vec3(-0.1,-0.1,1)
    : vec3(scale*(2.0*iMouse.xy-iResolution.xy)/iResolution.y,1);
  vec3 col = diagram(p,P);
  fragColor = vec4(pow(col,vec3(0.4545)),1);
}

/*

This shader illustrates how an electromagnetic wave reflects
and refracts at an interface. It was used as an illustration
in the talk at Revision 2020, "Implementing a Physically
Based Shading without locking yourself in" :

https://bit.ly/2IZqYET

The directions of the reflected and refracted waves are not
hard coded, but instead a consequence of the interferences
at the interface.

The shader is inspired by Fabrice Neyret's
https://www.shadertoy.com/view/4sSBRd

--
Zavie

*/


// Settings:
#define SHOW_INCIDENT_WAVE 1
#define SHOW_REFLECTED_WAVE 1
#define SHOW_TRANSMITTED_WAVE 1

#define NUMBER_OF_SAMPLES 256
#define WAVE_LENGTH 32 // Wave length, in pixels
#define ENABLE_ROUGHNESS 1
#define ENABLE_DISTANCE_ATTENUATION 1 // Not sure which is correct. :-/
#define ENABLE_WAVE_HIGHLIGHTING 0 // Doesn't work yet.

// Index of refraction of both media:
#define N1 1.0
#define N2 1.5

// Color to distinguish the two media:
#define bgColor vec3(0.05, 0.2, 0.4)
#define matColor vec3(0.4, 0.2, 0.05)

// Colors to distinguish incident, reflected and
// transmitted waves:
#define iColor vec3(1.0, 0.8, 0.6)
#define rColor vec3(1.0, 0.2, 0.1)
#define tColor vec3(1.0, 0.7, 0.0)

#define PI acos(-1.)

// ---8<---------------------------------------------------------
// Implementation starts here.

float getHeight(float x)
{
    float a = texture(iChannel0, vec2(x+0.5, 0.)).r;
#if ENABLE_ROUGHNESS
    float roughness = smoothstep(1., 0., abs(x * 2. - 1.));
    roughness *= roughness;
#else
    float roughness = 0.;
#endif
    float h = ((a-0.5)*0.2*roughness + 0.5);
    return h * iResolution.y/iResolution.x;
}

float getWave(float d, float t, float waveLength, float speed, float phase)
{
    float x = (d - t*speed + phase);
#if ENABLE_WAVE_HIGHLIGHTING
    float intensity = pow(fract(x), 5.);
#else
    float intensity = 1.;
#endif
    return sin(2. * PI * x / waveLength) * intensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y/iResolution.x;

    float height = getHeight(uv.x);
    float fAbove = smoothstep(-0.001, 0.001, uv.y-height);
    bool isAbove = (fAbove > 0.5);
    float t = iTime * 50. / iResolution.x;

    // Incoming wave:
    vec2 sourceDirection = iMouse.xy / iResolution.xy - vec2(0.5, 0.);
    vec2 sourcePosition = 0.5 + 100. * normalize(sourceDirection);
    float iWave = 0.;
#if SHOW_INCIDENT_WAVE
    if (isAbove)
    {
    	float iWaveLength = float(WAVE_LENGTH) / iResolution.x / N1;
    	iWave += getWave(length(uv - sourcePosition), t, iWaveLength, 1./N1, 0.) * fAbove;
    }
#endif

    // Reflected and transmitted waves:
    float rWave = 0.;
    float tWave = 0.;
    for (int i = 0; i < NUMBER_OF_SAMPLES; ++i)
    {
        float x = float(i) / max(1., float(NUMBER_OF_SAMPLES-1));
        float y = getHeight(x);

        vec2 p = vec2(x, y);
        float di = length(p - sourcePosition) * N1;
        float drt = length(uv - p);

#if ENABLE_DISTANCE_ATTENUATION
	    float attenuation = drt;
#else
    	float attenuation = 0.1;
#endif

#if SHOW_REFLECTED_WAVE
        if (isAbove)
        {
	        float rWaveLength = float(WAVE_LENGTH) / iResolution.x / N1;
            rWave += getWave(drt, t, rWaveLength, 1./N1, di/N1) * fAbove / attenuation;
        }
#endif
#if SHOW_TRANSMITTED_WAVE
        if (!isAbove)
        {
    	    float tWaveLength = float(WAVE_LENGTH) / iResolution.x / N2;
	        tWave += getWave(drt, t, tWaveLength, 1./N2, di/N2) * (1.-fAbove) / attenuation;
        }
#endif
    }
    rWave /= float(NUMBER_OF_SAMPLES);
    tWave /= float(NUMBER_OF_SAMPLES);

    vec3 color = mix(matColor, bgColor, fAbove);
    color = mix(color, iColor, clamp(iWave, 0., 1.));
    color = mix(color, rColor, clamp(rWave, 0., 1.));
    color = mix(color, tColor, clamp(tWave, 0., 1.));

    // Output to screen
    fragColor = vec4(pow(color, vec3(1./2.2)),1.0);
}

