// analytical raytracing speres with phong shading and reflections
// this is written for clarity, not for performance.

const float INFINITY = 1e20;
const float PI = 3.1415;

// sphere locations. radius is equal to z
const uint SPHERE_COUNT = 10u;
vec4 spheres[SPHERE_COUNT] = vec4[](
    vec4(0.0, 0.0, -100.0, 100.0),
    vec4(0.0, 0.0, 0.8, 0.8),
    vec4(1.0, 1.0, 0.4, 0.4),
    vec4(1.0, -1.0, 0.4, 0.4),
    vec4(-1.0, 1.0, 0.4, 0.4),
    vec4(-1.0, -1.0, 0.4, 0.4),
    vec4(1.5, 0.0, 0.1, 0.1),
    vec4(0.0, 1.5, 0.1, 0.1),
    vec4(-1.5, 0.0, 0.1, 0.1),
    vec4(0.0, -1.5, 0.1, 0.1)
);

// ambient, diffuse, specular, RGB
vec3 AMBIENT = vec3(0.001);
vec3 DIFFUSE = vec3(0.95, 0.95, 0.8) * 0.5;
vec3 SPECULAR = vec3(0.95, 0.95, 0.8);
float SHININESS = 500.;
vec3 REFLECT = vec3(0.5);

// light, RGB. light is an isentropic light as I'm lazy.
vec3 LIGHT = vec3(1.0);
vec3 LIGHT_DIR = normalize(vec3(0.0, -1.0, -0.5)); 

// random function
float rand(float seed){
    return fract(sin(seed * 12.9898) * 43758.5453);
}

// testing stuff: slightly, randomly distrubs a direction vector
vec3 disturb(vec3 dir) {
    vec3 noise = vec3(rand(dir.x), rand(dir.y), rand(dir.z));
    return normalize(dir + 0.1 * noise);
}

// calculate analytic collision with the geometry.
vec3 collide(vec3 origin, vec3 dir, out float depth) {
    depth = INFINITY;
    uint match;

    // try colliding with the spheres, keeping the lowest depth entry
    for (uint i = 0u; i < SPHERE_COUNT; i++) {
        // early-z, just by projected center distance
        vec3 s = spheres[i].xyz - origin;
        float t = dot(dir, s);
        if (t > depth || t < 0.0) {
            continue;
        }

        // collision test
        vec3 d = (dir * t - s);
        if (length(d) > spheres[i].w) {
            continue;
        }

        // store any useful data for the actual collision calc
        depth = t;
        match = i;
    }
    if (depth == INFINITY) {
        return vec3(0.);
    }

    // math
    vec3 s = spheres[match].xyz - origin;
    float t = depth;
    vec3 d = (dir * t - s);
    // this line is numerically a bit unstable due to the subtraction of two large numgers which can be close together
    vec3 r = d - dir * sqrt(spheres[match].w * spheres[match].w - dot(d, d));

    // surface normal and final depth
    depth = length(s + r);
    return normalize(r);
}

vec3 light_ray(vec3 orig, vec3 dir, out vec3 hit, out vec3 hit_norm) {
    // hit something
    float depth;
    hit_norm = collide(orig, dir, depth);
    hit = orig + depth * dir;

    // shadow
    float shadow_depth;
    collide(hit, -LIGHT_DIR, shadow_depth);

    // lighting
    vec3 light = vec3(0.0);
    if (depth < 1e9) {
        light += AMBIENT;
        if (shadow_depth > 1e9) {
            light += DIFFUSE * dot(-LIGHT_DIR, hit_norm);
            light += SPECULAR * pow(max(0.0, dot(reflect(-LIGHT_DIR, hit_norm), dir)), SHININESS);
        }
    }
    return light * LIGHT;
}

vec3 camera;
vec3 camera_dir;
vec3 camera_up;
const float AR = 1.4;

// this part is identical for every pixel
void setup() {
    camera = vec3(5.0 * cos(iTime * 0.1), 5.0 * sin(iTime * 0.1), 2.);
    camera_dir = normalize(vec3(0., 0., 1.) - camera);
    camera_up = vec3(0., 0., 1.);


    LIGHT_DIR = normalize(vec3(cos(iTime), -sin(iTime), -0.5)); 

    for (uint i = 1u; i < SPHERE_COUNT; i++) {
        spheres[i].z += 0.5 * cos(float(i) * 456.0 + iTime) + 0.5;
    }
}

vec3 calcpixel(vec2 uv) {
    // setup ray dir, norm vector >
    vec3 camera_x = normalize(cross(camera_dir, camera_up));
    // setup ray dir, norm vector ^
    vec3 camera_y = normalize(cross(camera_x, camera_dir));
    // final ray dir
    vec3 ray_dir = normalize(camera_dir * AR + camera_x * uv.x + camera_y * uv.y);

    // cast ray
    vec3 hit, hit_norm;
    vec3 light = light_ray(camera, ray_dir, hit, hit_norm);

    // cast reflection ray
    vec3 reflect_dir = reflect(ray_dir, hit_norm);
    disturb(reflect_dir);
    vec3 reflect_hit, reflect_norm;
    vec3 reflection = light_ray(hit, reflect_dir, reflect_hit, reflect_norm);

    // cast reflection reflection ray
    vec3 rreflect_dir = reflect(reflect_dir, reflect_norm);
    disturb(rreflect_dir);
    vec3 rreflect_hit, rreflect_norm;
    vec3 rreflection = light_ray(reflect_hit, rreflect_dir, rreflect_hit, rreflect_norm);

    // I'm bored and my fps is still too high
    vec3 rrreflect_dir = reflect(rreflect_dir, rreflect_norm);
    disturb(rrreflect_dir);
    vec3 rrreflect_hit, rrreflect_norm;
    vec3 rrreflection = light_ray(rreflect_hit, rrreflect_dir, rrreflect_hit, rrreflect_norm);


    // assemble
    return light + (reflection + (rreflection + rrreflection * REFLECT) * REFLECT) * REFLECT;
}

const uint AA = 2u;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // setup scene parameters
    setup();


    vec3 color = vec3(0.);
    for (uint i = 0u; i < AA; i++) {
        for (uint j = 0u; j < AA; j++) {
            // setup screen coord system. up = (0, 1), down = (0, -1), right = (AR, 0), left = (-AR, 0)
            vec2 coord = fragCoord + vec2(i, j) / float(AA);
            vec2 uv = (coord - iResolution.xy * 0.5) / iResolution.y;

            color += calcpixel(uv);
        }
    }
    color /= float(AA * AA);

    // gamma correct and write to output
    fragColor = vec4(pow(color, vec3(1. / 2.2)), 1.);
}

