vec2 march(vec3 ro, vec3 rd, inout vec3 p, inout float t, inout bool hit){
	vec2 d = map(ro);

    if(d.x < 0.3)
        ro += rd*0.05;

    hit = false; t = 0.; p = ro;

    for(float i = 0.; i< 150.; i++){
