////////////////////////////////////////////////////////////////////////////////
//
// The Kummer Surfaces, raymarched using the implicit equation as SDF,
// adjusted by the analytic gradient.
//
// Matthew Arcus, 2020.
//
// <mouse>: rotate
// <up>/<down>: move in/out
// <pageup>/<pagedown>: select surface parameter (or allow to vary).
// <left>/<right>: fog distance or clip radius
// c: clip at clipradius sphere
// f: fog
// l: light transform
// r: autorotation
// s: soft shadows
// x: clip at x-plane
//
// https://mathworld.wolfram.com/KummerSurface.html//
//
// The Kummer surfaces are a family of surfaces, parametrized on 'mu'.
// For mu^2 = 1/3, the surface is a (double) sphere and for mu^2
// = 1, Steiner's Roman Surface; for mu^2 > 1, the surface becomes
// infinite, assuming various forms as mu^2 increases. All the
// surfaces have tetrahedral symmetry.
//
// Style somewhat inspired by Man Ray's photographs of mathematical
// models, taken in the 1930's (at the suggestion of Max Ernst):
//
// http://photographmag.com/reviews/man-ray-human-equations-a-journey-from-mathematics-to-shakespeare/
//
// (the lead photo is in fact of the Kummer surface, with a different projection).
//
// The soft shadows can be a little flaky (eg. if the light ray becomes part of
// the object) and slow things down a bit, but the raymarching works ok
// out to about mu2 = exp(26.0), with a camera distance of exp(14.0).
//
////////////////////////////////////////////////////////////////////////////////

float AA = 2.0; // 1.0 for slow machines

// Man Ray chiaroscuro
const vec3 bgcol = vec3(0.01);
const vec3 tropecol = vec3(0);
const vec3 basecol = vec3(1);
const vec3 clipcol = 0.1*basecol;
vec3 light = vec3(4,1,1);
float ambient = 0.05;

int maxsteps = 200;
float maxdist = 25.0;
float precis = 0.00001;
float time = 0.0;

bool dorotate = true; // Autorotate
bool doclipradius = false;
float clipradius = 2.5;
bool doclipx = false;

// The Kummer Surface. https://mathworld.wolfram.com/KummerSurface.html
float mu2 = 1.0;
vec4 kummer4(vec3 pos) {
  // Return derivative in xyz, function value in w.
  float x = pos.x, y = pos.y, z = pos.z, w = 1.0;
  float K = 1.414213562;
  float p = w-z-K*x;
  float q = w-z+K*x;
  float r = w+z+K*y;
  float s = w+z-K*y;
  float t = x*x+y*y+z*z-mu2*w*w;
  float lambda = (3.0*mu2-1.0)/(3.0-mu2);
  float dx = 4.0*t*x - lambda*K*r*s*(p-q);
  float dy = 4.0*t*y - lambda*K*p*q*(s-r);
  float dz = 4.0*t*z - lambda*(-q*r*s-p*r*s+p*q*s+p*q*r);
  float k = t*t - lambda*p*q*r*s;
  return vec4(dx,dy,dz,k);

}

// Store sign of implicit function at ray origin here - if negative
// then we are 'inside' surface and DE and normals must be reversed.
float rosign = 1.0;

float eval(vec3 p, out vec3 n, out int type) {
  vec4 t = kummer4(p);
  n = t.xyz;
  float d = t.w/length(n); // Scaled distance
  type = 0;
  if (doclipradius) {
    float d1 = length(p)-clipradius;
    if (d1 > d) { type = 1; n = p; d = d1; }
  }
  if (doclipx) {
    float d1 = -p.x;
    if (d1 > d) { type = 2; n = vec3(-1,0,0); d = d1; }
  }
  n *= rosign;
  return rosign*d;
}

float eval(vec3 p) { int type; vec3 n; return eval(p,n,type); }

float march(vec3 ro, vec3 rd) {
  float t = 0.1;
  for(int i = 0; i < maxsteps; i++) {
    //assert(i < 50);
    vec3 p = ro + t*rd;
    float d = eval(p);
    if (abs(d) < t*precis) break;
    // Longer distance estimates can be inaccurate,
    // so clamp to avoid overstepping.
    d = min(d,max(0.5,0.1*t));
    t += d;
    if (t < 0.0 || t > maxdist) break;
  }
  return t;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    p.xz = -rotate(p.xz,time*0.15);
    //p.yz = rotate(p.yz,0.25*PI);
    //p.yz = rotate(p.yz,time*0.125);
  }
  return p;
}

// softshadow from iq.
float softshadow(vec3 ro, vec3 rd, float mint, float tmax, float w) {
  float t = mint;
  float res = 1.0;
  for (int i = 0; i < 200; i++) {
    float h = eval(ro + t*rd);
    res = min(res, h/(w*t));
    t += clamp(h, 0.01*t, max(0.5,0.1*t));
    if (res < -1.0 || t > tmax) break;
  }
  res = max(res,-1.0); // clamp to [-1,1]
  res = 0.25*(1.0+res)*(1.0+res)*(2.0-res); // smoothstep
  return res;
}

float getmu2(int n) {
  int noptions = 6, i = 0;
  n = mymod(n,noptions);
  if (n == i++) return 0.33334 + 2.5*(1.0-cos(0.2*time));
  if (n == i++) return 1.0;
  if (n == i++) return 2.0;
  if (n == i++) return 12.0;
  if (n == i++) return 100.0;
  if (n == i++) return 0.33334 + exp(0.1*time)-1.0;
  return 1.0;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  time = iTime;
  // Interesting values of mu2 start at 1/3.
  mu2 = getmu2(keycount(KEY_PAGE_DOWN)-keycount(KEY_PAGE_UP));
  dorotate = !key(CHAR_R);
  doclipradius = key(CHAR_C);
  doclipx = key(CHAR_X);
  float dscale = exp(0.1*float(keycount(KEY_RIGHT)-keycount(KEY_LEFT)));
  maxdist *= dscale;
  clipradius *= dscale;
  float camera = 4.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  maxdist = max(maxdist,2.0*camera); // Ensure maxdist is sensible.
  vec3 ro = vec3(0,0,-camera);
  ro = transform(ro);
  rosign = sign(eval(ro)); // Get sign at ray origin
  if (!key(CHAR_L)) light = transform(light);
  vec3 aacol = vec3(0);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 rd = vec3(uv,2);
      rd = transform(rd);
      rd = normalize(rd);
      float t = march(ro,rd);
      vec3 col = bgcol;
      if (t > 0.0 && t < maxdist) {
        vec3 p = ro+t*rd;
        int type; vec3 n;
        eval(p,n,type);
        // Sharp edge between clip region and surface means
        // sometimes we get the wrong normal.
        if (type == 0 && dot(n,rd) > 1e-2) {
          type = 1; n = p;
        }
        n = normalize(n);
        if (type != 0) {
          col = clipcol;
        } else {
          col = basecol;
          //p.xy = abs(p.xy); // Exploit this symmetry? Or general tetrahedral symmetry?
          // Draw some lines
          float d = 1e8;
          // Planes through edge and centre of opposite edge of tetrahedron.
          float K = sqrt(2.0);
          // Vertices of base tetrahedron
          vec3 v0 = vec3(K,0,-1);
          vec3 v1 = vec3(-K,0,-1);
          vec3 v2 = vec3(0,K,1);
          vec3 v3 = vec3(0,-K,1);
          // 6 edges, sphere (radius mu) intersects each edge in 2 places, eg:
          //|kv0 + (1-k)v1)| = mu
          //|k(v0-v1)+v1| = mu
          //(k(v0-v1)+v1).(k(v0-v1)+v1) = mu2 =
          // k2(v0-v1).(v0-v1)+2k(v0-v1).v1+v1.v1 -mu2 = 0
          // A = (v0-v1).(v0-v1)
          // B = (v0-v1).v1
          // C = v1.v1-mu2
          // This can probably be simplified
          float A = dot(v0-v1,v0-v1); // v0.v0 - 2v0.v1 + v1.v1
          float B = dot(v0-v1,v1);    // v0.v1 - v1.v1
          float C = dot(v1,v1)-mu2;   // v1.v1 - mu2
          float D = B*B-A*C;
          if (D >= 0.0) {
            D = sqrt(D);
            float k = -(B+D)/A;
            vec3 v01 = k*v0+(1.0-k)*v1;
            vec3 v10 = k*v1+(1.0-k)*v0;
            vec3 v02 = k*v0+(1.0-k)*v2;
            vec3 v20 = k*v2+(1.0-k)*v0;
            vec3 v03 = k*v0+(1.0-k)*v3;
            vec3 v30 = k*v3+(1.0-k)*v0;

            vec3 v12 = k*v1+(1.0-k)*v2;
            vec3 v21 = k*v2+(1.0-k)*v1;
            vec3 v13 = k*v1+(1.0-k)*v3;
            vec3 v31 = k*v3+(1.0-k)*v1;
            vec3 v23 = k*v2+(1.0-k)*v3;
            vec3 v32 = k*v3+(1.0-k)*v2;

            d = min(d,planedist(p,v01,v02,v13)); // v23 also in plane
            d = min(d,planedist(p,v01,v03,v12)); // v32 also in plane
            d = min(d,planedist(p,v02,v03,v21)); // etc.

            d = min(d,planedist(p,v10,v12,v03));
            d = min(d,planedist(p,v10,v13,v02));
            d = min(d,planedist(p,v12,v13,v20));

            d = min(d,planedist(p,v20,v21,v03));
            d = min(d,planedist(p,v20,v23,v01));
            d = min(d,planedist(p,v21,v23,v10));

            d = min(d,planedist(p,v30,v31,v02));
            d = min(d,planedist(p,v30,v32,v01));
            d = min(d,planedist(p,v31,v32,v10));
          }
          d = min(d,planedist(p,v0,v1,v2));
          d = min(d,planedist(p,v0,v1,v3));
          d = min(d,planedist(p,v0,v2,v3));
          d = min(d,planedist(p,v1,v2,v3));
          col = mix(tropecol,col,0.1+0.9*smoothstep(0.0,0.0005,d/t));
        }
        //vec3 lightdir = normalize(light); // Light at infinity
        vec3 lightdir = normalize(light-p);
        float diffuse = (1.0-ambient)*max(0.0,dot(n,lightdir));
        if (key(CHAR_S)) diffuse *= softshadow(p,lightdir,0.01,4.0*max(1.0,length(p)),0.1);
        col *= ambient+diffuse;
        float fog = key(CHAR_F) ? 0.0 : t/maxdist;
        col = mix(col,bgcol,fog);
      }
      aacol += col;
    }
  }
  aacol /= float(AA*AA);
  aacol = pow(aacol,vec3(0.4545));
  fragColor = vec4(aacol,1);
  if (check) fragColor.r = 1.0;
}

