////////////////////////////////////////////////////////////////////////////////

const float PI = 3.14159;
bool check = false;
void assert(bool b) {
  if (!b) check = true;
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

// Distance of p from plane through q,r,s.
float planedist(vec3 p, vec3 q, vec3 r, vec3 s) {
  vec3 n = normalize(cross(q-r,q-s));
  return abs(dot(p-q,n));
}

vec3 hsv2rgb(float h, float s, float v) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
  return v * mix( vec3(1.0), rgb, s);
}

int mymod(int n, int m) {
  // glsl % undefined for -ve arguments
  if (n < 0) return m-1-(-n-1)%m;
  else return n%m;
}

const int CHAR_A = 65;
const int CHAR_C = 67;
const int CHAR_F = 70;
const int CHAR_L = 76;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
