  t = 10. + 5. * sin  (0.1 * 2. * pi * t);
  for (int k = 0; k < 12; k ++) {
    if (k >= nIt) break;
    p += 0.4 * sin (1.7 * p.yzx / f + f * t);
    f *= 0.75;
  }
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 blbRad;
  float d, t;
  q = p;
  q.xz = Rot2D (q.xz, 0.1 * tCur);
  t = 0.5 * tCur;
  blbRad = vec2 (1.1 + 0.31 * sin (t + 1.31), 1. + 0.41 * sin (1.7 * (t + 1.31)));
  d = SmoothMin (PrBoxDf (SMap (q - vec3 (0.7, 0., 0.), 1.11 * t + 0.7), vec3 (blbRad.x)),
     PrBoxDf (SMap (q - vec3 (-0.7, 0., 0.), 1.3 * t), vec3 (blbRad.y)), 0.2);
  return 0.25 * d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 220; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec4 ObjNfL (vec3 p)
{
  vec4 v;
  vec2 e;
  float lp, s;
  e = vec2 (0.01, -0.01);
  for (int j = 0; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  s = dot (v, vec4 (1.));
  lp = (s - 2. * v.x - 4. * ObjDf (p)) / (e.x * e.x); // (laplacian: see "Cheap curvature" by nimitz)
  return vec4 (normalize (2. * v.yzw - s), lp);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, nl4;
  vec3 col, vn;
  float dstObj, lp, nDotL;
  nIt = 5 + int (0.5 * (idPage - 1.));
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    nl4 = ObjNfL (ro);
    vn = nl4.xyz;
    lp = nl4.w;
    col = (mod (idPage, 2.) == 0.) ?
       col = HsvToRgb (vec3 (0.35 * (1. - clamp (0.3 * lp, -1., 1.)), 0.8, 1.)) :
       vec3 (0.9) * (1. - step (10., lp));
    nDotL = max (dot (vn, ltDir), 0.);
    nDotL *= nDotL;
    col = col * (0.2 + 0.8 * nDotL * nDotL +
       0.2 * pow (max (dot (normalize (vn - rd), vn), 0.), 32.));
    rd = reflect (rd, vn);
  } else {
    col = vec3 (0.5, 0.5, 0.55);
  }
  return clamp (col, 0., 1.);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

void ExMain  (out vec3 fCol, in vec2 fCoord)
{
  mat3 vuMat;
  vec3 ro, rd;
  float el, az, zmFac;
  el = -0.15 * pi;
  az = 0.2 * pi * sin (0.05 * pi * tCur);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -40.);
  dstFar = 100.;
  zmFac = 8.;
  rd = vuMat * normalize (vec3 (fCoord, zmFac));
  ltDir = normalize (vec3 (0., 1., -1.));
  fCol = clamp (ShowScene (ro, rd), 0., 1.);
}

float ExObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, rRad, hRad, hOff, np, bc, thFac, nRing, y, s, w;
  dMin = dstFarEx;
  rRad = 0.4;
  hRad = 0.08;
  nRing = 3.;
  hOff = 0.15;
  thFac = 2.2;
  np = float (nPage - 1);
  q = p;
  s = mod (nRing, 2.);
  w = pgSize.z / nRing;
  bc = q.z;
  bc = mod (bc + s * w, 2. * w) - w;
  for (int k = 0; k < nPage; k ++) {
    q = p;
    q.y -= 0.5 * np * thFac * pgSize.y;
    y = q.y + np * thFac * pgSize.y * phsTurn;
    if (k == 0) q.xy = Rot2D (vec2 (q.x, y), -2. * pi * phsTurn);
    else q.y -= thFac * pgSize.y * (phsTurn - float (k));
    d = hRad - length (vec2 (q.x - rRad, bc));
    q.x -= pgSize.x - hOff + rRad;
    d = max (PrRoundBoxDf (q, pgSize - pgSize.y, pgSize.y), d);
    DMINQ (k + 1);
  }
  q = p;
  d = abs (q.z) - pgSize.z;
  q.z = bc;
  d = max (PrTorusDf (q, 0.5 * hRad, rRad + 0.3 * hRad), d);
  DMINQ (nPage + 1);
  return dMin;
}

float ExObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ExObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFarEx) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  v = vec4 (- ExObjDf (p + e.xxx), ExObjDf (p + e.xyy), ExObjDf (p + e.yxy), ExObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ExObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = 0; j < 30; j ++) {
    h = ExObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.7 + 0.3 * sh;
}

vec3 ExShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, c, vn;
  vec2 s, w;
  float dstObj, sh, npCyc, tpCyc, tpWait, nDotL, t;
  bool isImg;
  pgSize = vec3 (2.5, 0.011, 2.5);
  tpCyc = 3.;
  tpWait = 4.;
  t = (tpBook < 0.) ? (- tpBook * float (nPage - 1) * (tpCyc + tpWait)) :
     mod (tpBook - tpWait, float (nPage) * (tpCyc + tpWait));
  npCyc = mod (floor (t / (tpCyc + tpWait)), float (nPage));
  phsTurn = min (mod (t / (tpCyc + tpWait), 1.) * (tpCyc + tpWait) / tpCyc, 1.);
  isImg = false;
  dstObj = ExObjRay (ro, rd);
  if (dstObj < dstFarEx) {
    ro += dstObj * rd;
    vn = ExObjNf (ro);
    nDotL = max (dot (vn, ltDirEx), 0.);
    if (idObj <= nPage) {
      idPage = mod ((float (idObj - 1) + npCyc), float (nPage)) + 1.;
      c = HsvToRgb (vec3 (floor (0.5 * (idPage - 1.)) / float (nPage / 2), 0.7, 1.));
      s = pgSize.xz - abs (qHit.xz);
      col4 = vec4 (0.9, 0.9, 0.8, 0.2);
      if (qHit.y > 0.) {
        w = qHit.xz / pgSize.z;
        if (min (s.x, s.y) > 0.3) {
          isImg = true;
          ExMain (col, w);
        } else if (min (s.x, s.y) > 0.27) {
          col4 = vec4 (0.6 * c, 0.2);
        } else {
          col4 = vec4 (c, 0.2);
        }
      } else {
        col4 = vec4 (c, 0.2);
      }
    } else if (idObj == nPage + 1) {
      col4 = vec4 (0.9, 0.9, 0.95, 0.2);
      nDotL *= nDotL;
    }
    sh = ExObjSShadow (ro + 0.001 * vn, ltDirEx);
    if (isImg) col *= 0.2 + 0.8 * sh;
    else col = col4.rgb * (0.2 + 0.8 * sh * nDotL) +
       col4.a * step (0.95, sh) * pow (max (dot (normalize (ltDirEx - rd), vn), 0.), 32.);
  } else {
    col = vec3 (1., 1., 0.9) * (0.5 + 0.2 * rd.y);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tpBook = tCur;
  az = 0.;
  el = -0.5 * pi;
  if (mPtr.z > 0.) {
    tpBook = - clamp (1.05 * mPtr.x + 0.5, 0., 1.);
    el += pi * mPtr.y * smoothstep (0.1, 0.15, abs (mPtr.y));
  }
  vuMat = StdVuMat (el, az);
  ro = vec3 (1., 0., -18.);
  ro = vuMat * ro;
  zmFac = 7.;
  dstFarEx = 40.;
  ltDirEx = normalize (vec3 (1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (vec2 (- uv.x, uv.y) + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ExShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (1.)), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

