vec4 fractal (vec2 U) {
	U = 4.*U-2.+vec2(.5,-.3);
    vec2 z = U;
    for (float i = 0.; i < 20.;i++) {
    	z = vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y)-U;
        if (length(z)>4.) return 0.5+0.5*sin(vec4(1,2,3,4)+.1*i);
    }
    return vec4(1);

}
vec3 norm (float i) {
	vec2 u = unpack(i);
    vec4
        q = A(shape(u)),
        n = A(shape(u+vec2(0,O.y))),
        e = A(shape(u+vec2(O.x,0))),
        s = A(shape(u-vec2(0,O.y))),
        w = A(shape(u-vec2(O.x,0)));
    return normalize(vec3(
        length(e-q)-length(w-q),
        length(n-q)-length(s-q),
        1
    ));
}
