vec4 Qout (vec2 u) {
    vec2 U = gl_FragCoord.xy;
    vec4 Q = vec4(0);
	vec4 b = B(u);
    vec3 a = P(b.x).xyz;
    vec3 aa = P(b.y).xyz;
    vec3 aaa = P(b.z).xyz;
    vec3 c = color(b.x);
    vec3 cc = color(b.y);
    vec3 ccc = color(b.z);

    vec3 n = norm(b.x);
    vec3 nn = norm(b.y);
    vec3 nnn = norm(b.z);
    vec3 by = bary(U,a.xy,aa.xy,aaa.xy);
    Q.xyz = (by.x*c+by.y*cc+by.z*ccc);
    Q = fractal(Q.xy);
    Q.xyz *= 0.5+0.5*(by.x*n+by.y*nn+by.z*nnn).x;
    if (triangle(U,b)>1.) Q = 0.5+0.1*D(U);
    Q.w = 1.;
    return Q;
}
Main {
