float tri (vec2 U, vec4 a, vec4 aa, vec4 aaa) {
    if (length(a.xy-aaa.xy)<1e-3||length(aa.xy-aaa.xy)<1e-3||length(a.xy-aa.xy)<1e-3) return 1e3;
    float ab = ssg(U,a.xy,aa.xy),
          bc = ssg(U,aa.xy,aaa.xy),
          ca = ssg(U,aaa.xy,a.xy),
          l = min(abs(ab),min(abs(bc),abs(ca))),
          s = (ab<0.&&bc<0.&&ca<0.)||(ab>0.&&bc>0.&&ca>0.)?-1.:1.;
	if (s*l>=0.) return l*s;
   	ab=abs(ab)*sg(aaa.xy,a.xy,aa.xy);
    bc=abs(bc)*sg(a.xy,aa.xy,aaa.xy);
    ca=abs(ca)*sg(aa.xy,aaa.xy,a.xy);
