// Hierarchical Sort
void comp (inout vec4 Q, inout vec4 r, float l, float i) {
	if (l<r.x) {
        Q.yz = Q.xy;
        r.yz = r.xy;
        Q.x = i;
        r.x = l;
    } else if (l<r.y&&l>r.x) {
        Q.z = Q.y;
        r.z = r.y;
        Q.y = i;
        r.y = l;
    } else if (l<r.z&&l>r.y) {
        Q.z = i;
        r.z = l;
    }
}
void X (inout vec4 Q, inout vec4 r, vec2 U, vec2 u) {
        vec4 b,c;float l,i;
        if (iFrame%I==0) {
            i = pack(U+u); 
            b = B(U+u);
            l = triangle(U,b);
            comp(Q,r,l,i);
            i = Q.w; 
            b = B(unpack(Q.w));
            l = triangle(U,b);
            comp(Q,r,l,i);
        } else {
            c = C(U+u);
            b = B(unpack(c.x));
            i = c.x;
            l = triangle(U,b);
            comp(Q,r,l,i);
            b = B(unpack(c.y));
            i = c.y;
            l = triangle(U,b);
            comp(Q,r,l,i);
            b = B(unpack(c.z));
            i = c.z;
            l = triangle(U,b);
            comp(Q,r,l,i);
        }

}
void Y(inout vec4 Q, inout vec4 r, vec2 U, vec2 u) {
	float i, l; vec4 c, b;
    c = C(U+u);
    b = B(unpack(c.x));
    i = c.x;
    l = triangle(U,b);
    if (l<r.w) {
    	r.w = l;
        Q.w = i;
    }
    b = B(unpack(c.w));
    i = c.w;
    l = triangle(U,b);
    if (l<r.w) {
    	r.w = l;
        Q.w = i;
    }

}
Main {
    Q = C(U);
    vec4 r = vec4(
        triangle(U,B(unpack(Q.x))),
        triangle(U,B(unpack(Q.y))),
        triangle(U,B(unpack(Q.z))),
        triangle(U,B(unpack(Q.w)))
    );
