    vec4 q = A(U);
    float m = (U.y<20.*R.y)?5.:1.;
    for (int x = -5; x<=5; x++) {
        Q.xyz += force(U,vec2(x,0),q)/m;
        Q.xyz += force(U,vec2(0,x),q)/m;
    }
    Q.y -= m*6e-3;
    Q.xyz *= 0.999;

    if (U.y>R.y-20.) {
        Q.xyz *= 0.5;
    	U = shape(U);
    	vec4 p = vec4(U,0,0);
        p.x = 0.8*(p.x-0.5*R.x)+0.5*R.x;
       p.y += 20.;
        float a = 0.5*sin(6e-3*float(iFrame));

        p.x -= 0.5*R.x;
        p.xz *= ro(pi2*a);
        p.x += 0.5*R.x;
        if (iMouse.z>0.) {
            p.xy += iMouse.xy/R*200.-100.;
            Q.xyz *= 0.5;
        }
        Q.xyz += (p.xyz-q.xyz);
    }
}

