#define dotrad 10

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y < 10. || fragCoord.x < 10. || fragCoord.x > iResolution.x-10. || fragCoord.y > iResolution.y-10.) {
        return;
    }
    vec2 uv = fragCoord.xy / iResolution.xy;
    float thickness = 0.;
    float thickness_r = 0.;
    float thickness_d = 0.;
    vec2 vel;
    float count = 0.;
    // Output to screen
    for (int i = -dotrad; i < dotrad; i++) {
        for (int j = -dotrad; j < dotrad; j++) {
            vec4 tex = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(i,j), 0);
            if (tex.x == 0.) {
                continue;
            }
            float str = length(vec2(i,j));
            float str_r = length(vec2(float(i)+1.,j));
            float str_d = length(vec2(i,float(j)+1.));

    		thickness += 1./(str*str);
    		thickness_r += 1./(str_r*str_r);
    		thickness_d += 1./(str_d*str_d);
            vel += tex.zw;
            count++;
        }
    }
    float thickness_base = clamp(thickness, 0., .5)*2.;
    thickness_r = clamp(thickness_r, 0., .5)*2.;
    thickness_d = clamp(thickness_d, 0., .5)*2.;
    vec2 thickness_grad = (vec2(thickness_r, thickness_d)-thickness_base);
    vec4 bg = texture(iChannel1, uv-thickness_grad*.1);
    vec3 base_color = vec3(vel*.2/max(1.,count) + .5,1);
    fragColor = vec4(mix(bg.rgb,base_color,thickness > 0.1 ? .5 : 0.), 1);

}

