// modified version of
// http://byteblacksmith.com/improvements-to-the-canonical-one-liner-glsl-rand-for-opengl-es-2-0/
float rand(vec3 co)
{
    float a = 12.9898;
    float b = 78.233;
    float d = 283.8574;
    float c = 43758.5453;
    float dt= dot(co ,vec3(a,b,d));
    float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

// on this buffer xy = particle position, zw = particle velocity
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool hasPoint = false;
    ivec2 curVec = ivec2(fragCoord);
    for (int i = -MAX_V; i <= MAX_V; i++) {
        for (int j = -MAX_V; j <= MAX_V; j++) {
            ivec2 ij = ivec2(i,j);
            vec4 tex = texelFetch(iChannel0, curVec+ij, 0);
			vec4 force = texelFetch(iChannel1, curVec+ij, 0);
            if (iMouse.z > 0.) {
                vec2 diff = iMouse.xy - fragCoord.xy;
                float len = length(diff);
                if (len < 150.) {
                    force.xy += .2*normalize(diff);
                }
            }
            tex.zw += force.xy;
            tex.z = min(max(tex.z, -MAX_Vd),MAX_Vd);
            tex.w = min(max(tex.w, -MAX_Vd),MAX_Vd);
            if (!hasPoint && tex.x != 0. && int(tex.z+tex.x) == curVec.x && int(tex.w+tex.y) == curVec.y) {
                fragColor = tex + vec4(tex.zw,0,G);
                hasPoint = true;
            }
        }
    }
    if (!hasPoint) {
		vec4 dedup = texelFetch(iChannel2, curVec+ivec2(1,0), 0);
        if (dedup.x != 0. || dedup.y != 0.) {
            vec4 tex = texelFetch(iChannel0, ivec2(int(dedup.x),int(dedup.y)), 0);
            vec4 force = texelFetch(iChannel1, ivec2(int(dedup.x),int(dedup.y)), 0);
            tex.zw += force.xy;
            tex.z = min(max(tex.z, -MAX_Vd),MAX_Vd);
            tex.w = min(max(tex.w, -MAX_Vd*.9),MAX_Vd*.9);
            fragColor = tex + vec4(tex.zw,0,G);
        }
    }
    if (fragCoord.y < 20.) {
        fragColor.w += 0.15*(20.-fragCoord.y);
    }
    if (fragCoord.x < 20.) {
    	fragColor.z += 0.15*(20.-fragCoord.x);
    }
    if (fragCoord.x > iResolution.x-20.) {
    	fragColor.z -= 0.15*(fragCoord.x + 20. - iResolution.x);
    }
    if (fragCoord.y > iResolution.y-20.) {
    	fragColor.w -= 0.15*(fragCoord.y + 20. - iResolution.y);
    }
    if (iFrame == 2) {
        if (curVec.x < 200 && curVec.x > 50 && curVec.y > 50 && (curVec.x + curVec.y) % 3 == 1 && curVec.y % 3 == 1 ) {
            fragColor = vec4(fragCoord.xy,0,0);
