    if (texelFetch(iChannel3, ivec2(32,0), 0).x > 0.5 && rand(vec3(iTime,fragCoord)) > .995 && fragCoord.x < 150. && fragCoord.x > 50. && fragCoord.y > 400.) {
        fragColor = vec4(fragCoord.xy, 0,0);
    }
}

