// This is free and unencumbered software released into the public domain.
// https://unlicense.org/UNLICENSE

#define RANK_1           6
#define RANK_2           7
#define RANK_3           9
#define ZOOM_SPEED_1     2.
#define ZOOM_SPEED_2     3.
#define ZOOM_SPEED_3     5.
#define ROTATE_SPEED_1  10.
#define ROTATE_SPEED_2  -5.
#define ROTATE_SPEED_3   3.
#define SATURATION_1     1.0
#define SATURATION_2     0.5
#define SATURATION_3     0.75
#define POINT_SIZE       0.0002

/* library */

const float PI = radians(180.f);
const float TAU = radians(360.f);

float length2(vec2 p) { return p.x * p.x + p.y * p.y; }
int idiv(int a, int b) { return (a + (a > 0 ? b - 1 : 0)) / b; }

vec3 hsv2rgb(float h, float s, float v) {
    h = fract(h) * 6.;
    float c = v * s;
    float x = c * (1. - abs(mod(h, 2.) - 1.));
    float m = v - c;
    switch (int(h)) {
        case 6:
        case 0: return m + vec3(c, x, 0);
        case 1: return m + vec3(x, c, 0);
        case 2: return m + vec3(0, c, x);
        case 3: return m + vec3(0, x, c);
        case 4: return m + vec3(x, 0, c);
        case 5: return m + vec3(c, 0, x);
    }
}

bool keyToggle(int key) {
    return texelFetch(iChannel0, ivec2(key, 2), 0).x > 0.;
}

/* voronoi */

float res_d = 1./0.;
vec3 res_color = vec3(0, 0, 0);
vec2 p;
int debug_rings = 0;

void add_point(vec2 pos, vec3 color) {
    float distance_from_center = length2(p);
    float d = length2(pos - p.xy);
    if (res_d < d)
        return;
    res_d = d;
    res_color = !keyToggle(80/*p*/) && d < distance_from_center * POINT_SIZE
        ? color * 0.8
        : color * atan(distance_from_center / d / 2.) / PI * 2.;
}

// This function adds an infinity number of nested rings to the Voronoi Diagram.
// The ring is a group of points at the same radius. Each ring consists of
// `rank` points.
//
// The trick is, for each pixel, this function is checking only three possible
// points: one in the ring above, one in the ring bellow, and one in two rings
// bellow.
void endless(int rank, float zoom, float rotation, float sat) {
    if (rank <= 2) return;
    float ring_dist = (1. + sin(PI / float(rank))) / cos(PI / float(rank));
    zoom *= float(rank);

    int center_ring = int(floor(log(length2(p)) / 2. / log(ring_dist) - zoom));
    float alpha = (atan(p.y, p.x) / TAU - rotation) * float(rank);
    float alpha1 = floor(alpha + 0.5) / float(rank);
    float alpha2 = floor(alpha) / float(rank);

    debug_rings += center_ring;
    for (int ring = center_ring - 1; ring <= center_ring + 1; ring++) {
        float shift = float(abs(ring) % 2) / 2. / float(rank);
        float color_shift = float(idiv(3 * ring, 2)) / float(rank);
        float d = abs(ring) % 2 == 0 ? alpha1 : alpha2;
        float t = (rotation + shift + d) * TAU;
        vec2 pos = pow(ring_dist, float(ring) + zoom) * vec2(cos(t), sin(t));
        add_point(pos, hsv2rgb(d + color_shift, sat, 1.));
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    p.xy = (fragCoord.xy - iResolution.xy / 2.) * pow(2., 20. * iMouse.x / iResolution.x);
    float rot = iTime / 1000.;
    float zoom = iTime / 100.;

    if (!keyToggle(49/*1*/))
        endless(RANK_1, zoom * ZOOM_SPEED_1, rot * ROTATE_SPEED_1, SATURATION_1);
    if (!keyToggle(50/*2*/))
        endless(RANK_2, zoom * ZOOM_SPEED_2, rot * ROTATE_SPEED_2, SATURATION_2);
    if (keyToggle(51/*3*/))
        endless(RANK_3, zoom * ZOOM_SPEED_3, rot * ROTATE_SPEED_3, SATURATION_3);

    float debug_shade = keyToggle(68/*d*/) ? float(debug_rings % 2) / 4. + 0.5 : 1.;

    fragColor = vec4(res_color, 1.) * debug_shade;
}

////////////////////////////////////////////////////////////////////////////////
//
// Project an Apollonian gasket onto a sphere.
//
// Since (reverse) stereographic projection preserves circles (it's an inversion)
// we can use it to project a planar Apollonian gasket onto a sphere.
//
// The gasket is generated from an n-sided prism and the resulting pattern is
// based on the dual double ended n-sided pyramid (except for n = 3 where
// both patterns are tetrahedra).
//
// <mouse>: apply a Mobius transform to the sphere
// <up>/<down>: move in and out
// <left>/<right>: change number of circles
// b: show background
// c: color cycle
// r: autorotation
// s: show sphere
//
////////////////////////////////////////////////////////////////////////////////

const float AA = 2.0;
const int maxiterations = 50;

int NCIRCLES = 5;      // 3 or more
bool dobackground = true;
bool docolorcycle = false;
bool dorotate = true;
bool dosphere = true;

bool dotetrahedron = false;

const float PI = 3.14159265;

vec3 hsv2rgb(vec3 c);
vec3 transform(in vec3 p);

// If z is inside the circle, invert to the outside.
// s is 1 or -1, and sets sense of "inside"
bool checkinverse(inout vec2 z, vec3 circle, float s) {
  vec2 c = circle.xy;  // Centre of circle
  float r2 = circle.z; // Radius squared
  vec2 p = z-c;
  float p2 = dot(p,p);
  if (s*p2 > s*r2) return false;
  z = p*r2/p2 + c;
  return true;
}

// A ring of circles, centred on the unit circle, with
// incircle and outcircle. No outcircle for tetrahedron.
bool checkcircles(inout vec2 z) {
  float theta = PI/float(NCIRCLES);
  float r = 1.0/cos(theta);
  float s = tan(theta);
  vec3 c1 = vec3(0,0,pow(r-s,2.0));
  vec3 c2 = vec3(0,0,pow(r+s,2.0));
  if (checkinverse(z,c1,1.0)) return true;
  if (!dotetrahedron && checkinverse(z,c2,-1.0)) return true;
  for (int i = 0; i < NCIRCLES; i++) {
    float t = 2.0*float(i)*theta-0.5*PI;
    vec3 c = vec3(r*cos(t), r*sin(t), s*s);
    if (checkinverse(z,c,1.0)) return true;
  }
  return false;
}

// r*(1+sqrt(2)) = 1 => r = 1/(1+sqrt(2))
vec3 gasket(vec2 z){
  // 3 circles can either be a tetrahedron, with some
  // special casing, or a triangular prism (with dual
  // a double triangular pyramid).
  if (dotetrahedron) z /= 2.0; // Scale for tetrahedron.
  for(int n = 0; n < maxiterations; n++){
    if (!checkcircles(z)) return vec3(z,n);
  }
  return vec3(z,maxiterations);
}

vec3 gasketcolor(vec2 z) {
  vec3 data = gasket(z);
  float threshold = 9.0;
  float t = docolorcycle ? 0.1*iTime : 0.0;
  return hsv2rgb(vec3(t+data.z/float(maxiterations),1,1));
}

// R3 inversion in sphere, centre q, square radius r2.
vec3 invert(vec3 p, vec3 q, float r2) {
  p -= q;
  p *= r2/dot(p,p);
  p += q;
  return p;
}

vec3 stereographic0(vec3 p) {
  return invert(p,vec3(0,0,-1),2.0);
}

// Invert in sphere radius sqrt(2), centre (0,0,-1)
// is stereographic projection from the unit sphere
// to z=0 (and its inverse).
vec2 stereographic(vec3 p) {
  return stereographic0(p).xy;
}

vec3 istereographic(vec2 z) {
  return stereographic0(vec3(z,0));
}

vec3 getspherecolor(vec3 p) {
  // Apply inversion to sphere.
  if (true) {
    vec3 q = vec3(0);
    if (iMouse.x > 0.0) {
      vec3 m = vec3((2.0*iMouse.xy-iResolution.xy)/iResolution.y,0);
      q = vec3(m.xyz);
    }
    // Any point is the centre of a sphere orthogonal to the
    // unit sphere, inverting in which leaves the unit sphere
    // invariant. Any point inverts to another point on the
    // line to the sphere centre, so just find other
    // intersection of ray p to r with unit sphere.
    // |p + kr|^2 = 1 where r = q-p
    // (p.p + 2kp.r + k^2(r.r)) = 1
    // 2p.r + k(r.r) = 0 since p.p = 1 & dividing by k
    vec3 r = q-p;
    float k = -2.0*dot(p,r)/dot(r,r);
    p += k*r;
  }
  p = transform(p); // autorotation
  // And stereographic projection from (0,0,1) to plane z = 0
  return gasketcolor(stereographic(p));
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  vec3 p;       // centre
  float r;      // radius
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit[2]) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // Formula for x^2 + 2Bx + C = 0
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t0,t1;
  if (B >= 0.0) {
    t0 = -B-D; t1 = C/t0;
  } else {
    t1 = -B+D; t0 = C/t1;
  }
  hit[0] = Hit(t0,(q+t0*d-p)/r);
  hit[1] = Hit(t1,(q+t1*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit[2]) {
  Sphere s = Sphere(vec3(0),1.0);
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;

vec3 solve(Ray r,vec2 uv) {
  Hit hit[2];
  vec3 c = vec3(0);
  if (!dosphere || !intersectScene(r,hit)) {
    if (dobackground) {
      vec3 p = istereographic(uv);
      c = getspherecolor(p);
      c *= dosphere ? 0.2 : 0.8;
    }
  } else {
    for (int i = 0; i < 2; i++) {
      vec3 n = hit[i].n;
      if (dot(r.d,n) > 0.0) n *= -1.0;
      vec3 basecolor = getspherecolor(hit[i].n);
      if (basecolor == vec3(0)) continue;
      vec3 color = vec3(0);
      float ambient = 0.5;
      float diffuse = 0.5;
      color += basecolor*ambient;
      color += basecolor*diffuse*max(0.0,dot(light,n));
      float specular = pow(max(0.0,dot(reflect(light,n),r.d)),5.0);
      vec3 speccolor = basecolor; //vec3(1);
      color += 0.5*specular*speccolor;
      return color;
    }
  }
  return c;
}

const int CHAR_B = 66;
const int CHAR_C = 67;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;

const int KEY_LEFT = 37;
const int KEY_UP = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN = 40;

bool key(int code) {
  return texelFetch(iChannel0, ivec2(code,2),0).x != 0.0;
}

vec4 store(int i,int j) {
  return texelFetch(iChannel1, ivec2(i,j),0);
}

int keycount(int key) {
  return int(store(0,key).x);
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec2 rotate(vec2 p, float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (false && iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  dobackground = !key(CHAR_B);
  docolorcycle = key(CHAR_C);
  dorotate = !key(CHAR_R);
  dosphere = !key(CHAR_S);
  NCIRCLES = max(3,5+keycount(KEY_RIGHT)-keycount(KEY_LEFT));
  dotetrahedron = NCIRCLES == 3;

  light = vec3(0.5,1.0,1.0);
  light = normalize(light);

  vec3 p = vec3(0,0,6);
  p.z *= exp(-0.1*float(keycount(KEY_UP)-keycount(KEY_DOWN)));
  vec3 col = vec3(0);
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv,-6);
      r = normalize(r);
      col += solve(Ray(p,r),uv);
    }
  }
  fragColor = vec4(pow(col/(AA*AA),vec3(0.4545)),1);
}

