//learn from https://thebookofshaders.com/12/?lan=ch
//Then learn from https://iquilezles.org/articles/voronoilines

#define ANIMATE

vec2 random2( vec2 p ) {
    return fract(sin(
        vec2(dot(p,vec2(127.1,311.7)),
             dot(p,vec2(269.5,183.3)))
    )*43758.5453);
}

vec3 voronoi(in vec2 uv) {

	//
    //Tile the space
    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);

    //----------------------------------
    // 
    // first pass: regular vornoi
    //----------------------------------

    //
    vec2 minVector, nearNeighbor;

    //
    float minDistance = 8.0;

    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
        	//
            //Neigbor place in the grid
            vec2 curNeighbor = vec2(float(x), float(y));

            //
            //Random position from current + neighbor place in the grid
            vec2 neighborPoint = random2(i_uv + curNeighbor);

            #ifdef ANIMATE
            neighborPoint = 0.5 + .5*sin(iTime + 3.14159 * neighborPoint);
            #endif

            //
            //Vector from the pixel to point
            vec2 curVector = curNeighbor + neighborPoint - f_uv;

            //
            //calculate distance
            float curDistance = dot(curVector, curVector);

            //
            if(curDistance < minDistance) {
            	minDistance = curDistance;
                minVector = curVector;
                nearNeighbor = curNeighbor;
            }
        }
    }

    //----------------------------------
    // 
    // second pass: distance to borders
    //----------------------------------

    minDistance = 8.0;
    for (int y = -2; y <= 2; y++) {
        for (int x = -2; x <= 2; x++) { 
        	//
            //Neighbor'Neighbor
            vec2 curNeighbor = nearNeighbor + vec2(float(x), float(y));

            vec2 neighborPoint = random2(i_uv + curNeighbor);

            #ifdef ANIMATE
            neighborPoint = 0.5 + .5*sin(iTime + 3.14159 * neighborPoint);
            #endif

            vec2 curVector = curNeighbor + neighborPoint - f_uv;

            //
            if(dot(minVector - curVector, minVector - curVector)>0.00001)
            	//
                minDistance = min(minDistance, 
                         dot(0.5 * (minVector + curVector),
                             normalize(curVector - minVector)
                            ));

        }
    }

    return vec3(minDistance, minVector);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xx;

    vec3 c = voronoi( 8.0*uv );

    //
    //Show isolines
    vec3 addColor= vec3(0.3, .6, 0.6);
    vec3 color = c.x * (.5 + .5*sin(32.0*c.x))*vec3(1.0);

    //
    //Draw broders
    color = mix(addColor, color, smoothstep(.04, .07, c.x));

    //
    //Draw cell center
    float dd = length(c.yz);
	color = mix(addColor, color, smoothstep(0.0, 0.12, dd) );
    color += addColor * (1.0-smoothstep( 0.0, 0.04, dd));


    fragColor = vec4(color,1.0);
}

// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.


#define METHOD 2  // try method 1 and method 2


// procedural noise from IQ
vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			 dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
	const float K1 = 0.366025404; // (sqrt(3)-1)/2;
	const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

	vec2 a = p - i + (i.x+i.y)*K2;
	vec2 o = (a.x>a.y) ? vec2(1.0,0.0) : vec2(0.0,1.0);
	vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

	vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

	return dot( n, vec3(70.0) );
}

float fbm(vec2 uv)
{
	float f;
	mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
	f  = 0.5000*noise( uv ); uv = m*uv;
	f += 0.2500*noise( uv ); uv = m*uv;
	f += 0.1250*noise( uv ); uv = m*uv;
	f += 0.0625*noise( uv ); uv = m*uv;
	f = 0.5 + 0.5*f;
	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;

    // changing offset magnitude over time :
    float maxMag = 1./50.;
    float timeFreqFactor = 2.;
    float timeNoiseFactor = 1.2;
    float sinPower = 6.;
    float noiseTimeFactor = 10.;
    float offsetMag = maxMag * pow( sin(iTime*timeFreqFactor + timeNoiseFactor*noise( vec2(0.,iTime*noiseTimeFactor) ) ), sinPower );
    //offsetMag = maxMag;

    // changing offset pos over time :
    float timeChgFreq = .1;
    float nbDifferentPos = 10.;
