vec2 WarpCoord0(float p, float s)
{
    bool c = fract(p * 0.5 - 0.25) < 0.5;

    if(c) s = 1.0 - s;

    float p0 = floor(p - 0.5);
    float l0 =      (p - 0.5) - p0;

    if(l0 < s)
    {
        l0 *= 0.5 / s;
    }
    else
    {
        p0 += 1.0;
        l0 = (l0 - s) / (1.0 - s) * 0.5 + 0.5; 
    }

    if(c) l0 += 1.0;

    l0 = fract(l0 + 0.5);

    return vec2(p0, l0);
}

vec2 WarpCoord(vec2 uv, float s)
{
    vec2 u = WarpCoord0(uv.x, s);
    vec2 v = WarpCoord0(uv.y, s);

    return vec2(u.x + u.y, v.x + v.y);
}

float WarpCoordI0(float p, float s)
{
    if(fract(p * 0.5 - 0.25) < 0.5) 
        s = 1.0 - s;

    float p0 = floor(p - 0.5);
    float l0 =      (p - 0.5) - p0;

    if(l0 > s) p0 += 1.0;

    return p0;
}

vec2 WarpCoordI(vec2 uv, float s)
{
    return vec2(WarpCoordI0(uv.x, s), WarpCoordI0(uv.y, s));
}


