int savanna_biome(inout int color1, ivec4 neighbors){
    int GREEN = 0;
    int BLUE = 1;
    int GRAY = 2;
    int YELLOW = 3;
    int FOREST_GREEN = 4;
    return (color1 == GREEN && is_next_to(GRAY,neighbors))
    	? YELLOW
    :(color1 == YELLOW && is_next_to(GRAY,neighbors))
    	? FOREST_GREEN
    :(color1 == GRAY)
        ? GREEN
    :(color1 == BLUE && is_next_to(GREEN,neighbors))
        ? GREEN
    :(color1 == FOREST_GREEN && is_next_to(GREEN,neighbors))
        ? GREEN
    : color1;
}

void biome(inout int color1, ivec4 neighbors,int biome){
    color1 = 
    	savanna_biome(color1,neighbors);
}

int biome(in vec2 fragCoord,float mag,float zoom,int colors[5],int biome1){
    fragCoord *= zoom;


    float num_colors = float(colors.length());

    int color1 = magnify(fragCoord,mag,num_colors);

    ivec4 neighbors;

    while(mag > 1.0){
    neighbors = get_neighbors(fragCoord,mag,num_colors);
    biome(color1, neighbors,biome1);
    mag -= 1.0;
    }
    return colors[int(color1)];;
}




int savanna_biome(in vec2 fragCoord,float mag,float zoom,int[5] colors){
    return biome(fragCoord,mag,zoom,colors,SAVANNA_BIOME);
}

int savanna_biome(in vec2 fragCoord,float mag,float zoom){
	return savanna_biome(fragCoord,mag,zoom,int[](GREEN,BLUE,GRAY,YELLOW,FOREST_GREEN));
}

