{
    float zoom = mag;
    fragCoord *= zoom;
    fragCoord = fragCoord + floor(iTime*100.0)*vec2(zoom,0.0);

    //choose the biome here
	int biome = savanna_biome(fragCoord,mag,1.0);

    fragColor = vec4(get_color(biome),1.0);
}

