// Image tab: Final render and frame


#define LIT_BUFFER (iChannel0)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    time = iTime;

    vec2 coord = fragCoord / iResolution.xy;


    fragColor = texture(LIT_BUFFER, coord);


    vec4 region = getRegion();

    // If not the active region,
    if (!(coord.x > region.x && coord.x < region.z && coord.y > region.y && coord.y < region.w))
    {
        // tint ice blue.
        fragColor = mix(fragColor, vec4(0.6, 0.9, 1.0, 1.0), 0.4);
    }

    // Draw frames
    for (int i = 0; i < 5; i++)
    {
        vec4 region = getRegion(i);

        if (coord.x > region.x && coord.x < region.z && coord.y > region.y && coord.y < region.w)
        {
            vec2 ep = vec2(1.0, iResolution.x / iResolution.y);
            vec4 epp = ep.xyxy * vec4(1.0, 1.0, -1.0, -1.0);

            vec4 frame = region + epp * 0.03;

            // One
            if (coord.x < frame.x || coord.x > frame.z || coord.y < frame.y || coord.y > frame.w)
            {
                fragColor = vec4(0.0);
            }

            frame -= epp * 0.005;

            // Two
            if (coord.x < frame.x || coord.x > frame.z || coord.y < frame.y || coord.y > frame.w)
            {
                fragColor = vec4(1.0);
            }

            frame -= epp * 0.01;

            // Three.
            if (coord.x < frame.x || coord.x > frame.z || coord.y < frame.y || coord.y > frame.w)
            {
                fragColor = vec4(0.0);
            }

            return;
        }
    }    
}

#undef LIT_BUFFER

